INSERT INTO `posts` (`id`, `title`, `content`, `user_id`, `is_public`, `createdate`, `theme`, `url_hinhanh`) VALUES
(54, 'Bài Viết về gia đình ', '<p>Nếu lần đầu v&agrave;o một ph&ograve;ng truyền thống của Nhật, bạn sẽ c&oacute; cảm gi&aacute;c &ecirc;m &aacute;i khi đi tr&ecirc;n lớp chiếu. M&ugrave;a h&egrave;, b&agrave;n ch&acirc;n sẽ cảm thấy m&aacute;t c&ograve;n m&ugrave;a đ&ocirc;ng cũng bớt đi lạnh lẽo. L&yacute; do ch&iacute;nh l&agrave; do thiết kế cẩn thận, tỉ mỉ của chiếu. Mỗi chiếc gồm 3 phần: L&otilde;i l&agrave;m từ rơm kh&ocirc; hoặc sợi h&oacute;a học; lớp ngo&agrave;i l&agrave; chiếu c&oacute;i; viền&nbsp;bằng vải.</p>\r\n\r\n<p>Lớp l&otilde;i đặc biệt d&agrave;y hơn 5 cm gi&uacute;p cho chiếu c&oacute; khả năng đ&agrave;n hồi tốt, c&aacute;ch nhiệt, th&iacute;ch hợp để bạn đi ch&acirc;n trần, ngồi, hay nằm. V&agrave;o m&ugrave;a h&egrave;, chủ nh&agrave; c&oacute; thể nằm, ngồi trực tiếp tr&ecirc;n chiếu. Sang đ&ocirc;ng, gia chủ chỉ cần trải một lớp đệm mỏng l&agrave; đủ &ecirc;m &aacute;i, đảm bảo sức khỏe.&nbsp;</p>\r\n', 6, 1, '2017-12-16 23:08:28', 'Gia Dinh', 'hinhanh/2017/12/Gia Dinh/giadinh4.jpg'),
(55, 'Những bài học từ Giáng sinh cha mẹ nào cũng nên nói với con', '<p><em>Dưới đ&acirc;y l&agrave; b&agrave;i viết của chị L&ecirc; Mai Hương, một nh&agrave; gi&aacute;o Montessori ở H&agrave; Nội, về những b&agrave;i học giản dị nhưng hữu &iacute;ch bố mẹ c&oacute; thể chia sẻ với con trong dịp Gi&aacute;ng sinh n&agrave;y:&nbsp;</em></p>\r\n\r\n<p>Đừng đưa c&aacute;c yếu tố t&ocirc;n gi&aacute;o v&agrave;o trong lễ Gi&aacute;ng sinh nếu bạn kh&ocirc;ng theo đạo, h&atilde;y đơn giản rằng đ&oacute; l&agrave; một lễ hội của gia đ&igrave;nh trong th&aacute;ng cuối c&ugrave;ng của năm v&agrave; mọi người được sum vầy,&nbsp; tận hưởng một m&ugrave;a lễ hội với rất nhiều b&agrave;i học nh&acirc;n văn.</p>\r\n\r\n<p>B&agrave;i học 1: Văn h&oacute;a phương T&acirc;y dạy về sự trao tặng</p>\r\n\r\n<p>Điển h&igrave;nh l&agrave; &ocirc;ng gi&agrave; Noel, một &ocirc;ng gi&agrave; trao qu&agrave; cho tất cả c&aacute;c em b&eacute; ngoan tr&ecirc;n thế giới. Triết l&yacute; sống được dạy cho c&aacute;c em b&eacute; l&agrave; h&atilde;y trao đi thật nhiều để mang lại niềm vui cho tất cả mọi người. Vậy th&ocirc;i.</p>\r\n\r\n<p>B&agrave;i học số 2: Bố mẹ ch&iacute;nh l&agrave; &ocirc;ng gi&agrave; Noel đ&oacute;</p>\r\n\r\n<p>Đến một l&uacute;c, c&aacute;c em b&eacute; nhận ra rằng bố mẹ m&igrave;nh ch&iacute;nh l&agrave; &ocirc;ng gi&agrave; Noel. Những em b&eacute; th&ocirc;ng minh nhận ra phần lớn những m&oacute;n qu&agrave; m&igrave;nh c&oacute; đều đến từ bố mẹ v&agrave; h&atilde;y tr&acirc;n trọng bố mẹ.&nbsp;</p>\r\n\r\n<p>B&agrave;i học số 3: Chỉ những em b&eacute; ngoan mới được nhận qu&agrave;</p>\r\n\r\n<p>Đ&acirc;y l&agrave; thuyết nh&acirc;n quả của mọi nền văn h&oacute;a thế n&ecirc;n nếu muốn c&oacute; được những m&oacute;n qu&agrave; tốt đẹp trong cuộc đời d&ugrave; l&agrave; b&acirc;y giờ hay sau n&agrave;y, con h&atilde;y thực sự nỗ lực, l&agrave; một em b&eacute; độc lập, ngoan ngo&atilde;n, biết lễ nghĩa. Mọi m&oacute;n qu&agrave; đều l&agrave; phần thưởng của sự nỗ lực trong một thời gian d&agrave;i.</p>\r\n\r\n<p>B&agrave;i học số 4: Chỉ tặng thứ m&agrave; trẻ con ao ước c&oacute; v&agrave; chỉ một m&agrave; th&ocirc;i&nbsp;</p>\r\n\r\n<p>H&atilde;y học c&aacute;ch trao tặng đ&uacute;ng c&aacute;i con m&igrave;nh mong ước v&agrave; với số lượng c&oacute; hạn th&ocirc;i. C&agrave;ng &iacute;t gi&aacute; trị c&agrave;ng cao. Như kim cương vậy, kim cương đắt v&igrave; sự khan hiếm. Khan hiếm c&aacute;c m&oacute;n qu&agrave; sẽ l&agrave;m gi&aacute; trị m&oacute;n qu&agrave; tăng l&ecirc;n.</p>\r\n\r\n<p>B&agrave;i học số 5: Phải n&oacute;i ra, phải viết được mong muốn của m&igrave;nh, đừng hy vọng người kh&aacute;c đọc được suy nghĩ của bạn</p>\r\n\r\n<p>Điều n&agrave;y nghe c&oacute; vẻ đơn giản nhưng thực sự để biết m&igrave;nh muốn g&igrave; đ&atilde; l&agrave; kh&oacute;, n&oacute;i chi tới chuyện thổ lộ hay viết ra để xin điều đ&oacute;. C&aacute;c em b&eacute; đ&atilde; được r&egrave;n th&oacute;i quen lắng nghe bản th&acirc;n từ nhỏ chắc chắn mai sau sẽ dễ d&agrave;ng thể hiện nhu cầu, nguyện vọng của m&igrave;nh v&agrave; l&agrave;m cho cuộc sống trở n&ecirc;n dễ d&agrave;ng hơn khi sống với mọi người xung quanh.</p>\r\n\r\n<p>B&agrave;i học số 6: Trao đi v&ocirc; điều kiện&nbsp;</p>\r\n\r\n<p>Khi bạn g&oacute;i m&oacute;n qu&agrave; đặt dưới gốc c&acirc;y th&ocirc;ng, điều duy nhất bạn muốn l&agrave; nụ cười của con l&uacute;c b&oacute;c qu&agrave; v&agrave; rằng con tin l&agrave; mọi điều ước đều th&agrave;nh hiện thực. L&agrave;m cha mẹ h&atilde;y thực sự trao đi v&ocirc; điều kiện chứ đừng r&agrave;ng buộc con v&igrave; những điều bố mẹ đ&atilde; phải hy sinh. Chỉ khi trao đi v&ocirc; điều kiện mới được gọi l&agrave; trao tặng, trao đi c&oacute; điều kiện gọi l&agrave; trao đổi. Hai điều đ&oacute; ho&agrave;n to&agrave;n kh&ocirc;ng giống nhau. Bố mẹ chỉ n&ecirc;n trao tặng để l&agrave;m mẫu văn h&oacute;a trao tặng cho con c&aacute;i noi theo sau n&agrave;y.</p>\r\n\r\n<p>B&agrave;i học số 7: Kể cả kh&ocirc;ng c&oacute; &ocirc;ng gi&agrave; Noel con vẫn ổn, con y&ecirc;u</p>\r\n\r\n<p>V&igrave; mọi điều ước kh&ocirc;ng th&agrave;nh hiện thực nếu con chẳng h&agrave;nh động để khiến n&oacute; xảy ra. Mọi thứ kh&ocirc;ng tự nhi&ecirc;n diễn ra m&agrave; đều l&agrave; th&agrave;nh quả lao động của ch&iacute;nh con. Con thấy đấy, bản th&acirc;n con đ&atilde; l&agrave; một ph&eacute;p m&agrave;u, kh&ocirc;ng cần đến &ocirc;ng gi&agrave; Noel. V&agrave; rồi sau n&agrave;y, ch&iacute;nh con lại l&agrave; ph&eacute;p m&agrave;u của con m&igrave;nh. Thế chẳng phải tuyệt diệu sao?</p>\r\n', 6, 1, '2017-12-16 23:10:38', 'Gia Dinh', 'hinhanh/2017/12/Gia Dinh/giadinh2.jpg'),
(53, 'Pháp Luật Ngày nay', '<p>Theo cơ quan điều tra, từ năm 2016 đến nay, H&ugrave;ng, Tiến v&agrave; Hạnh l&uacute;c th&igrave; đ&oacute;ng vai người đi kh&aacute;m bệnh, l&uacute;c giả l&agrave;m bệnh nh&acirc;n để tr&agrave; trộn v&agrave;o nh&agrave; xe của Bệnh viện Đa khoa huyện Hương Sơn. Chọn chiếc xe mới nhất, ch&uacute;ng d&ugrave;ng vam ph&aacute; kh&oacute;a rồi dắt ra ngo&agrave;i.</p>\r\n\r\n<p>Xe m&aacute;y sau khi trộm được đem b&aacute;n cho Trường để tuồn v&agrave;o miền Nam hoặc đưa đi c&aacute;c v&ugrave;ng kh&aacute;c ti&ecirc;u thụ.</p>\r\n\r\n<p>Nh&agrave; chức tr&aacute;ch c&aacute;o buộc, nh&oacute;m đạo ch&iacute;ch thực hiện tr&oacute;t lọt 16 vụ trộm xe m&aacute;y tại bệnh viện, g&acirc;y ra 15 vụ kh&aacute;c ở một số nơi kh&aacute;c tại Nghệ An. Tổng gi&aacute; trị t&agrave;i sản chiếm đoạt l&ecirc;n tới h&agrave;ng trăm triệu đồng.</p>\r\n\r\n<p>Mở rộng điều tra, cảnh s&aacute;t ph&aacute;t hiện, cuối năm 2016, H&ugrave;ng v&agrave; Hạnh đ&atilde; hai lần đột nhập nh&agrave; của c&ocirc; h&agrave;ng x&oacute;m gi&agrave;u c&oacute;, đục k&eacute;t sắt lấy trộm tiền v&agrave; v&agrave;ng gi&aacute; trị hơn 50 triệu đồng.</p>\r\n\r\n<p>Cảnh s&aacute;t thu hơn 30 chiếc xe m&aacute;y c&ugrave;ng nhiều tang vật li&ecirc;n quan.</p>\r\n\r\n<p>H&ugrave;ng từng c&oacute; một tiền &aacute;n về tội Trộm cắp t&agrave;i sản. Tiến l&agrave; em trai H&ugrave;ng, Trường l&agrave; cậu ruột của bộ đ&ocirc;i.</p>\r\n', 4, 1, '2017-12-16 23:00:03', 'Phap Luat', 'hinhanh/2017/12/Phap Luat/phapluat6.jpg'),
(52, 'Nữ giám đốc ngân hàng tham ô 2.600 lượng vàng xin hiến xác', '<h2>N&oacute;i lời sau c&ugrave;ng, cựu nữ gi&aacute;m đốc Agribank chi nh&aacute;nh Bến Th&agrave;nh - tham &ocirc; 2.600 lượng v&agrave;ng - xin giảm &aacute;n cho con rể, cấp dưới v&agrave; xin hiến x&aacute;c.</h2>\r\n\r\n<p>Ng&agrave;y 13/12, phi&ecirc;n xử Nguyễn Thị Ho&agrave;ng Oanh (57 tuổi, nguy&ecirc;n gi&aacute;m đốc Agribank Bến Th&agrave;nh, quận 1) v&agrave; 11 đồng phạm kết th&uacute;c phần tranh luận.</p>\r\n\r\n<p>Bị c&aacute;o buộc về h&agrave;nh vi tham &ocirc; 2.600 lượng v&agrave;ng v&agrave; gi&uacute;p sức cho doanh nghiệp chiếm đoạt hơn 5.600 lượng, trước đ&oacute; b&agrave; Oanh bị đề nghị &aacute;n tử h&igrave;nh về c&aacute;c tội Tham &ocirc; t&agrave;i sản, Nhận hối lộ, Vi phạm quy định về cho vay trong hoạt động của c&aacute;c tổ chức t&iacute;n dụng.</p>\r\n\r\n<p>N&oacute;i lời sau c&ugrave;ng, b&agrave; Oanh thừa nhận đ&atilde; l&agrave;m nhiều việc sai phạm g&acirc;y thiệt hại cho nh&agrave; băng, song b&agrave; xin t&ograve;a xem x&eacute;t về tội danh Tham &ocirc; t&agrave;i sản.</p>\r\n\r\n<p>&quot;Bị c&aacute;o đ&atilde; c&oacute; hơn 30 năm l&agrave;m Đảng vi&ecirc;n v&agrave; cống hiến. Giờ bị c&aacute;o đ&atilde; gi&agrave;, mong HĐXX gi&uacute;p để được hiến x&aacute;c cho y học sau khi thi h&agrave;nh &aacute;n. Bị c&aacute;o mong gia đ&igrave;nh chấp nhận v&agrave; thấu hiểu quyết định n&agrave;y&quot;, b&agrave; Oanh n&oacute;i v&agrave; cũng xin t&ograve;a xem x&eacute;t giảm nhẹ h&igrave;nh phạt cho con rể, cấp dưới. &nbsp;</p>\r\n\r\n<p>S&aacute;u bị c&aacute;o nguy&ecirc;n l&agrave; c&aacute;n bộ, nh&acirc;n vi&ecirc;n ng&acirc;n h&agrave;ng thừa nhận sai phạm trong nghiệp vụ do phải chịu &aacute;p lực chỉ đạo từ cấp tr&ecirc;n. Họ đề nghị t&ograve;a xem x&eacute;t về c&aacute;o buộc đồng phạm với Oanh trong tội Tham &ocirc;.&nbsp;</p>\r\n\r\n<p>L&agrave; người chủ mưu trong vụ lừa hơn 5.600 lượng v&agrave;ng của Agribank, L&ecirc; Văn T&iacute;nh&nbsp;(55 tuổi, gi&aacute;m đốc C&ocirc;ng ty TNHH Kim Gia Thuận)&nbsp;xin t&ograve;a giảm nhẹ h&igrave;nh phạt. Bị c&aacute;o&nbsp;k&ecirc;u oan về tội Đưa hối lộ v&agrave; kh&ocirc;ng thừa nhận c&aacute;o buộc đ&atilde; thỏa thuận chi tiền ch&ecirc;nh lệch cho Oanh để được k&yacute; hợp đồng vay v&agrave;ng.</p>\r\n\r\n<p>C&oacute; vai tr&ograve; đồng phạm với T&iacute;nh về h&agrave;nh vi lừa đảo, vợ con &ocirc;ng n&agrave;y xin t&ograve;a được hưởng &aacute;n treo. Họ đồng &yacute; để T&iacute;nh sử dụng t&ecirc;n để th&agrave;nh lập c&ocirc;ng ty với mục đ&iacute;ch gi&uacute;p l&agrave;m ăn chứ kh&ocirc;ng nhằm mục đ&iacute;ch lừa đảo.</p>\r\n\r\n<p>Trước đ&oacute;,&nbsp;<strong>luật sư của b&agrave; Oanh cho rằng th&acirc;n chủ kh&ocirc;ng phạm</strong>&nbsp;c&aacute;c tội Tham &ocirc; t&agrave;i sản v&agrave; Nhận hối lộ, chỉ chỉ sai s&oacute;t trong quy định về cho vay trong hoạt động của c&aacute;c tổ chức t&iacute;n dụng nhưng phạm tội do ho&agrave;n cảnh v&agrave; sức &eacute;p chi ti&ecirc;u.</p>\r\n\r\n<p>C&ograve;n luật sư của Agribank đề nghị HĐXX buộc Oanh v&agrave; T&iacute;nh c&oacute; tr&aacute;ch nhiệm bồi thường tổng cộng hơn 350 tỷ đồng thiệt hại cho nh&agrave; băng.</p>\r\n\r\n<p>Đại diện VKS giữ nguy&ecirc;n quan điểm c&aacute;o buộc b&agrave; Oanh đ&atilde; lợi dụng chức vụ, tr&aacute;ch nhiệm để chiếm đoạt t&agrave;i sản của Agribank nhằm thu lợi c&aacute; nh&acirc;n. Bị c&aacute;o đ&atilde; sử dụng c&aacute;c thủ đoạn như đưa người nh&agrave; v&agrave;o l&agrave;m ở vị tr&iacute; l&atilde;nh đạo, sử dụng nhiều ph&aacute;p nh&acirc;n, t&ecirc;n kh&aacute;ch h&agrave;ng khống để hợp thức h&oacute;a hợp đồng, r&uacute;t v&agrave;ng đ&aacute;o hạn g&acirc;y thiệt hại lớn.&nbsp;</p>\r\n\r\n<p>Bị c&aacute;o c&ograve;n chỉ đạo cấp dưới, bất chấp ph&aacute;p luật để thực hiện h&agrave;nh vi của m&igrave;nh, x&acirc;m phạm đến t&agrave;i sản của nh&agrave; nước. VKS cũng giữ nguy&ecirc;n quan điểm truy tố c&aacute;c bị c&aacute;o kh&aacute;c về tội danh như c&aacute;o trạng.</p>\r\n', 4, 1, '2017-12-16 22:54:35', 'Phap Luat', 'hinhanh/2017/12/Phap Luat/phapluat4.jpg'),
(51, 'Ông Đinh La Thăng có thêm hai luật sư bảo vệ', '<h2>Luật sư Nguyễn Huy Thiệp tham gia bảo vệ cho cả hai anh em &ocirc;ng Thăng v&agrave; đ&atilde; được cơ quan điều tra cấp giấy chứng nhận b&agrave;o chữa.</h2>\r\n\r\n<p>Tối 16/12, &ocirc;ng Nguyễn Huy Thiệp (Đo&agrave;n Luật sư H&agrave; Nội) cho biết đ&atilde; được nhận giấy chứng nhận b&agrave;o chữa cho &ocirc;ng Đinh La Thăng (nguy&ecirc;n chủ tịch hội đồng th&agrave;nh vi&ecirc;n Tập đo&agrave;n dầu kh&iacute; quốc gia Việt Nam, Ph&oacute; trưởng Ban Kinh tế Trung ương, nguy&ecirc;n ủy vi&ecirc;n Bộ Ch&iacute;nh trị).</p>\r\n\r\n<p>Ngo&agrave;i &ocirc;ng Thiệp, luật sư Đ&agrave;o Hữu Đăng cũng tham gia bảo vệ quyền v&agrave; lợi &iacute;ch cho &ocirc;ng Thăng.&nbsp;Trước đ&oacute;, &ocirc;ng Phan Trung Ho&agrave;i (Ph&oacute; Chủ tịch Li&ecirc;n đo&agrave;n Luật sư Việt Nam, th&agrave;nh vi&ecirc;n Đo&agrave;n luật sư TP HCM) đ&atilde; được cấp giấy chứng nhận b&agrave;o chữa cho &ocirc;ng Thăng.</p>\r\n\r\n<p>Theo luật sư Thiệp, &ocirc;ng c&ograve;n tham gia b&agrave;o chữa cho bị can<a href="https://vnexpress.net/tin-tuc/phap-luat/em-trai-ong-dinh-la-thang-bi-bat-3682067.html">&nbsp;Đinh Mạnh Thắng</a>(em trai &ocirc;ng Thăng, cựu chủ tịch HĐQT C&ocirc;ng ty dầu kh&iacute; S&ocirc;ng Đ&agrave;) v&agrave; đ&atilde; được cơ quan điều tra cấp giấy chứng nhận b&agrave;o chữa. Ng&agrave;y 16/12, luật sư đ&atilde; dự buổi hỏi cung &ocirc;ng Thắng.</p>\r\n\r\n<p>Theo nh&agrave; chức tr&aacute;ch, &ocirc;ng Thăng li&ecirc;n quan hai vụ &aacute;n kinh tế nghi&ecirc;m trọng đang được điều tra. Đ&oacute; l&agrave; vụ &aacute;n cố &yacute; l&agrave;m tr&aacute;i quy định của Nh&agrave; nước về quản l&yacute; kinh tế g&acirc;y hậu quả nghi&ecirc;m trọng, lạm dụng chức vụ, quyền hạn chiếm đoạt t&agrave;i sản, g&acirc;y thiệt hại 800 tỷ đồng trong việc g&oacute;p vốn của Tập đo&agrave;n dầu kh&iacute; quốc gia Việt Nam (PVN) v&agrave;o Ng&acirc;n h&agrave;ng Thương mại Cổ phần Đại Dương (OceanBank); vụ &aacute;n Cố &yacute; l&agrave;m tr&aacute;i quy định của Nh&agrave; nước về quản l&yacute; kinh tế g&acirc;y hậu quả nghi&ecirc;m trọng, Tham &ocirc; t&agrave;i sản xảy ra tại Tổng C&ocirc;ng ty X&acirc;y lắp dầu kh&iacute; (PVC) v&agrave; Dự &aacute;n Nh&agrave; m&aacute;y Nhiệt điện Th&aacute;i B&igrave;nh II.</p>\r\n\r\n<p>&Ocirc;ng Đinh Mạnh Thắng (55 tuổi) bị xử l&yacute; h&igrave;nh sự khi nh&agrave; chức tr&aacute;ch mở rộng điều tra vụ &aacute;n Tham &ocirc; t&agrave;i sản xảy ra tại C&ocirc;ng ty Cổ phần Bất động sản Điện lực Dầu kh&iacute; Việt Nam (PVPower Land) v&agrave; C&ocirc;ng ty Cổ phần Minh Ng&acirc;n.</p>\r\n', 4, 1, '2017-12-16 22:53:13', 'Phap Luat', 'hinhanh/2017/12/Phap Luat/phapluat1.jpg'),
(47, 'Học sinh giới thiệu sách trong giờ chào cờ', '<h2>Học sinh tự giới thiệu cuốn s&aacute;ch đ&atilde; đọc cho c&aacute;c bạn trong trường v&agrave;o giờ ch&agrave;o cờ đầu tuần l&agrave; hoạt động nhằm n&acirc;ng cao văn h&oacute;a đọc của Trường Tiểu học Li&ecirc;n Minh (Đức Thọ, H&agrave; Tĩnh).&nbsp;</h2>\r\n\r\n<p>Giờ ch&agrave;o cờ s&aacute;ng 28/11, hơn 300 học sinh Trường Tiểu học Li&ecirc;n Minh (Đức Thọ, H&agrave; Tĩnh) được nghe một nữ sinh lớp 5B chia sẻ về cuốn s&aacute;ch&nbsp;<em>H&ocirc;m nay con ngủ c&ugrave;ng mẹ</em>&nbsp;m&agrave; em đ&atilde; đọc tuần qua. Kh&ocirc;ng chỉ giới thiệu nội dung s&aacute;ch, nữ sinh c&ograve;n n&oacute;i về những điểm hấp dẫn của t&aacute;c phẩm.&nbsp;</p>\r\n\r\n<p>&quot;Hoạt động n&agrave;y nhằm khuyến kh&iacute;ch việc đọc cho học sinh to&agrave;n trường, được triển khai từ đầu th&aacute;ng 10/2016, cả hiệu trưởng v&agrave; gi&aacute;o vi&ecirc;n c&ugrave;ng tham gia&quot;, Hiệu trưởng Nguyễn Minh H&agrave;o n&oacute;i. Mỗi th&aacute;ng sẽ c&oacute; một chủ đề để giới thiệu s&aacute;ch, như th&aacute;ng 10 n&oacute;i về mẹ, th&aacute;ng 11 l&agrave; tấm l&ograve;ng cao cả của thầy c&ocirc;.&nbsp;</p>\r\n\r\n<p>Hoạt động giới thiệu s&aacute;ch ra đời sau khi trường Li&ecirc;n Minh tiếp nhận mấy trăm đầu s&aacute;ch từ chương tr&igrave;nh&nbsp;<em>S&aacute;ch h&oacute;a n&ocirc;ng th&ocirc;n.&nbsp;</em>C&aacute;c thầy c&ocirc; trăn trở l&agrave;m thế n&agrave;o đưa s&aacute;ch tới tận tay học sinh, khuyến kh&iacute;ch c&aacute;c em đọc khi văn h&oacute;a đọc của giới trẻ đang ng&agrave;y c&agrave;ng giảm s&uacute;t do chịu ảnh hưởng của văn h&oacute;a nghe nh&igrave;n.&nbsp;</p>\r\n\r\n<p>&quot;Mỗi s&aacute;ng ch&uacute;ng t&ocirc;i đều hỏi c&aacute;c em đ&atilde; đọc được bao nhi&ecirc;u cuốn s&aacute;ch. Những em đọc nhiều sẽ c&oacute; thưởng. Sau 2 th&aacute;ng thực hiện, số lượng học sinh đọc s&aacute;ch tăng l&ecirc;n&quot;, Hiệu trưởng H&agrave;o n&oacute;i. Hiện mỗi lớp cử ra một số học sinh l&ecirc;n giới thiệu s&aacute;ch.&nbsp;Nh&agrave; trường kỳ vọng, thời gian tới khi phong tr&agrave;o đọc tăng mạnh, sẽ c&oacute; th&ecirc;m nhiều học sinh chủ động tham gia.</p>\r\n', 3, 1, '2017-12-16 22:41:13', 'Giao Duc', 'hinhanh/2017/12/Giao Duc/giaoduc10.jpg'),
(46, 'Âm 46 độ C, học sinh Trung Quốc vượt gió rét đến trường', '<h2>Sương m&ugrave; v&agrave; gi&oacute; Bắc cực cản tầm nh&igrave;n tr&ecirc;n đường nhưng học sinh v&ugrave;ng Hulubuir vẫn kh&ocirc;ng nghỉ học.&nbsp;</h2>\r\n\r\n<p>H&ocirc;m thứ tư, mọi đ&agrave;i kiểm so&aacute;t trong khu vực&nbsp;Hulunbuir, ph&iacute;a đ&ocirc;ng bắc khu tự trị Nội M&ocirc;ng Cổ (Trung Quốc) ghi nhận nhiệt độ ở mức khoảng &acirc;m 40 độ C. C&aacute; biệt, một đ&agrave;i kiểm so&aacute;t đo được nhiệt độ xuống &acirc;m 46,6 độ C,&nbsp;<em>Next Shark&nbsp;</em>ng&agrave;y 15/12 đưa tin.</p>\r\n\r\n<p>Đường phố bị cản tầm nh&igrave;n bởi lớp sương m&ugrave; d&agrave;y đặc v&agrave; những cơn gi&oacute; Bắc cực rất mạnh. Tuy nhi&ecirc;n, c&aacute;c trường học ở đ&acirc;y vẫn hoạt động. Người d&acirc;n dường như đ&atilde; quen với tiết trời khắc nghiệt của m&ugrave;a đ&ocirc;ng n&ecirc;n tỏ ra kh&ocirc;ng mấy bận t&acirc;m.</p>\r\n\r\n<p>Trong khi đ&oacute;, ở một số v&ugrave;ng kh&aacute;c tại Trung Quốc, c&aacute;c trường học buộc phải tổ chức lớp học ngo&agrave;i trời v&igrave; thiếu phương tiện sưởi ấm trong nh&agrave;. Điều may mắn l&agrave; Trung Quốc vừa cho ph&eacute;p c&aacute;c th&agrave;nh phố đốt than trở lại, d&ugrave; chỉ tạm thời để đối ph&oacute; gi&aacute; r&eacute;t với chi ph&iacute; thấp.</p>\r\n\r\n<p>Nhiệt độ ở Hulunbuir vẫn c&aacute;ch xa so với kỷ lục của nơi lạnh nhất thế giới, l&agrave;ng Oymyakon&nbsp;thuộc v&ugrave;ng Yakutia, Siberia. Theo kỷ lục thế giới Guinness, ng&ocirc;i l&agrave;ng n&agrave;y từng đạt mức &acirc;m 68 độ C.&nbsp;</p>\r\n', 3, 1, '2017-12-16 22:40:06', 'Giao Duc', 'hinhanh/2017/12/Giao Duc/giaoduc9.jpg'),
(45, 'Hóa giải căng thẳng ở trường học nhờ công tác xã hội', '<h2>Ngăn ngừa bạo lực học đường, t&igrave;nh trạng bỏ học, gi&aacute;o dục kỹ năng sống&hellip; l&agrave; nhiệm vụ ch&iacute;nh của c&ocirc;ng t&aacute;c x&atilde; hội trong trường học.</h2>\r\n\r\n<p>Tại hội thảo quốc tế &ldquo;Giải ph&aacute;p ph&aacute;t triển c&ocirc;ng t&aacute;c x&atilde; hội trong trường học ở Việt Nam&rdquo; do Đại học Gi&aacute;o dục (Đại học Quốc gia H&agrave; Nội) tổ chức ng&agrave;y 15/12, nhiều người tỏ ra lo ngại trước c&aacute;c vấn đề học sinh gặp phải trong trường học, như: &aacute;p lực học tập, bạo lực học đường, m&acirc;u thuẫn với gi&aacute;o vi&ecirc;n...</p>\r\n\r\n<p>TS Nguyễn Thị H&agrave; Lan (Đại học Khoa học X&atilde; hội v&agrave; Nh&acirc;n văn) cho biết theo nghi&ecirc;n cứu của tổ chức UNICEF năm 2017 về&nbsp;<em>Sức khỏe t&acirc;m thần v&agrave; t&acirc;m l&yacute; x&atilde; hội của trẻ em v&agrave; thanh thiếu ni&ecirc;n ở Việt Nam</em>, phần lớn trong số 110 người tham gia phỏng vấn tại H&agrave; Nội, TP HCM v&agrave; Điện Bi&ecirc;n x&aacute;c định &aacute;p lực học tập ở trường l&agrave; một trong những nỗi lo ch&iacute;nh.</p>\r\n\r\n<p>Ngo&agrave;i những &aacute;p lực khi chưa l&agrave;m b&agrave;i tốt, bị điểm k&eacute;m hoặc khi c&oacute; c&aacute;c kỳ thi quan trọng, c&aacute;c em c&ograve;n cảm thấy stress trầm trọng hơn khi bị so s&aacute;nh điểm số với bạn kh&aacute;c v&agrave; phải ganh đua trong học tập. Điều n&agrave;y ảnh hưởng ti&ecirc;u cực tới sức khỏe t&acirc;m thần của c&aacute;c em, nhiều em bị trầm cảm.</p>\r\n\r\n<p>Một nghi&ecirc;n cứu kh&aacute;c về sức khỏe t&acirc;m thần của UNICEF năm 2017 khảo s&aacute;t với 402 học sinh cho thấy tỷ lệ trẻ c&oacute; dấu hiệu cảm x&uacute;c bất thường chiếm gần 20%, số học sinh c&oacute; vấn đề về h&agrave;nh vi, ứng xử như mất tự chủ, dễ nổi giận.</p>\r\n\r\n<p>Về bắt nạt tại trường học, một nghi&ecirc;n cứu được tiến h&agrave;nh năm 2014 với 3.000 học sinh tại 30 trường THCS v&agrave; THPT tại H&agrave; Nội cho thấy khoảng 80% từng bị bạo lực giới trong trường học &iacute;t nhất một lần. Trong đ&oacute;, bạo lực tinh thần như mắng chửi, đe dọa, đặt điều, sỉ nhục chiếm cao nhất 73%; bạo lực thể chất như t&aacute;t, x&ocirc; đẩy, đ&aacute;, bạt tai, đ&aacute;nh đập chiếm 41% v&agrave; bạo lực t&igrave;nh dụng như sờ, h&ocirc;n, hiếp d&acirc;m chiếm 19%.</p>\r\n\r\n<p>Ngo&agrave;i ra, m&acirc;u thuẫn với gi&aacute;o vi&ecirc;n hoặc c&aacute;n bộ kh&aacute;c trong trường cũng l&agrave; yếu tố khiến học sinh dễ bị trầm cảm, trong khi lạm dụng thể chất v&agrave; t&igrave;nh cảm từ gi&aacute;o vi&ecirc;n hoặc c&aacute;n bộ kh&aacute;c trong trường lại khiến c&aacute;c em dễ rơi v&agrave;o t&igrave;nh trạng lo &acirc;u.</p>\r\n\r\n<p>Từ thực tế tr&ecirc;n, b&agrave; Lan cho rằng cần ph&aacute;t triển c&ocirc;ng t&aacute;c x&atilde; hội học đường để đ&aacute;p ứng nhu cầu thực tiễn ở Việt Nam. C&ocirc;ng t&aacute;c n&agrave;y cần tập trung ở ba cấp độ: ph&ograve;ng ngừa; can thiệt; phục hồi v&agrave; ph&aacute;t triển.</p>\r\n\r\n<p>Ở cấp độ ph&ograve;ng ngừa, nh&acirc;n vi&ecirc;n l&agrave;m c&ocirc;ng t&aacute;c x&atilde; hội cần nhấn mạnh đến việc n&acirc;ng cao nhận thức của học sinh, phụ huynh, gi&aacute;o vi&ecirc;n v&agrave; nh&acirc;n vi&ecirc;n trường về ph&aacute;t triển t&acirc;m l&yacute; x&atilde; hội của học sinh v&agrave; những kh&oacute; khăn của họ tại trường học.</p>\r\n\r\n<p>Ở cấp độ can thiệp, c&aacute;c dịch vụ c&ocirc;ng t&aacute;c x&atilde; hội của trường c&oacute; thể giải quyết c&aacute;c rối loạn t&acirc;m l&yacute; v&agrave; t&acirc;m thần của học sinh th&ocirc;ng qua dịch vụ trực tiếp như tư vấn, quản l&yacute; trường hợp, c&aacute;c hoạt động nh&oacute;m. C&aacute;c nh&acirc;n vi&ecirc;n x&atilde; hội của trường cũng c&oacute; thể tổ chức việc huấn luyện cho phụ huynh học sinh để hỗ trợ v&agrave; hợp t&aacute;c với gi&aacute;o vi&ecirc;n trong việc gi&uacute;p đỡ c&aacute;c em l&agrave;m việc tại trường.</p>\r\n\r\n<p>Ở cấp độ phục hồi v&agrave; ph&aacute;t triển, c&ocirc;ng t&aacute;c x&atilde; hội của trường học c&oacute; thể li&ecirc;n quan đến việc cung cấp c&aacute;c hoạt động nh&oacute;m v&agrave; hỗ trợ tạo ra m&ocirc;i trường l&agrave;nh mạnh cho học sinh, sinh vi&ecirc;n.</p>\r\n\r\n<p>&ldquo;Kinh nghiệm quốc tế cho thấy c&aacute;c dịch vụ c&ocirc;ng t&aacute;c x&atilde; hội gi&uacute;p giải quyết c&aacute;c vấn đề ph&aacute;t sinh từ trường học. Do đ&oacute;, cần c&oacute; kế hoạch chiến lược để hỗ trợ ph&aacute;t triển c&ocirc;ng t&aacute;c x&atilde; hội của trường v&igrave; lợi &iacute;ch tốt nhất của học sinh&rdquo;, b&agrave; Lan n&oacute;i.</p>\r\n\r\n<p>GS.TS Nguyễn Qu&yacute; Thanh (Hiệu trưởng Đại học Gi&aacute;o dục)&nbsp;cho biết thực tế ở c&aacute;c nước tr&ecirc;n thế giới, c&ocirc;ng t&aacute;c x&atilde; hội trường học c&oacute; vai tr&ograve; quan trọng trong giải quyết vấn đề của nh&agrave; trường th&ocirc;ng qua qu&aacute; tr&igrave;nh t&aacute;c động v&agrave;o bốn đối tượng ch&iacute;nh l&agrave; học sinh, phụ huynh, thầy c&ocirc; gi&aacute;o v&agrave; c&aacute;n bộ quản l&yacute; gi&aacute;o dục.</p>\r\n\r\n<p>&ldquo;Ngăn ngừa bạo lực học đường, t&igrave;nh trạng bỏ học, giải quyết mối quan hệ bạn b&egrave;, thầy c&ocirc;, kết nối gia đ&igrave;nh v&agrave; nh&agrave; trường, gi&aacute;o dục c&aacute;c kỹ năng sống&hellip; l&agrave; những nhiệm vụ ch&iacute;nh của c&ocirc;ng t&aacute;c x&atilde; hội trong trường học&quot;, &ocirc;ng Thanh n&oacute;i v&agrave; cho rằng c&ocirc;ng t&aacute;c x&atilde; hội trong trường học cần được đẩy mạnh ở Việt Nam để ph&ugrave; hợp với thực tế hiện nay.</p>\r\n', 3, 1, '2017-12-16 22:38:36', 'Giao Duc', 'hinhanh/2017/12/Giao Duc/giaoduc6.jpg'),
(44, 'Mổ vá môi cho thiếu niên 16 tuổi suýt chết vì hủ tục dân tộc', '<h2>Ng&agrave;y 9/8 b&eacute; Tạ Y&ecirc;n Rẫy trải qua một giờ phẫu thuật tại Bệnh viện Nhi đồng 1 để đ&oacute;ng khe hở m&ocirc;i v&agrave; tạo h&igrave;nh mũi.</h2>\r\n\r\n<p><a href="https://suckhoe.vnexpress.net/tin-tuc/suc-khoe/va-ham-ech-cho-cau-be-sut-moi-suyt-chet-do-hu-tuc-3624385.html#ctr=related_news_click">V&aacute; h&agrave;m ếch cho cậu b&eacute; sứt m&ocirc;i su&yacute;t chết do hủ tục</a></p>\r\n\r\n<p>Qu&aacute; giờ cơm trưa, đ&oacute;i bụng nhưng người mẹ l&agrave; Tạ Y&ecirc;n Thị Mai vẫn kh&ocirc;ng muốn đi ăn v&igrave; lo lắng cho con. Ngồi b&ecirc;n ngo&agrave;i, &aacute;nh mắt chị d&otilde;i theo c&aacute;nh cửa ph&ograve;ng mổ, đợi đến khi được th&ocirc;ng b&aacute;o ca phẫu thuật th&agrave;nh c&ocirc;ng mới thở ph&agrave;o nhẹ nh&otilde;m.</p>\r\n\r\n<p>B&aacute;c sĩ Nguyễn Văn Đẩu, Trưởng khoa Răng H&agrave;m Mặt, Bệnh viện Nhi đồng 1 cho biết bệnh nh&acirc;n trải qua một giờ phẫu thuật đ&oacute;ng khe hở m&ocirc;i cho k&iacute;n lại v&agrave; tạo h&igrave;nh mũi. Sau ca mổ điều trị c&aacute;c dị h&igrave;nh b&ecirc;n ngo&agrave;i, dự kiến 3-6 th&aacute;ng sau bệnh nh&acirc;n sẽ được phẫu thuật&nbsp;đ&oacute;ng lỗ thủng v&ograve;m họng để c&oacute; nụ cười ho&agrave;n thiện.</p>\r\n\r\n<p>Tạ Y&ecirc;n Rẫy người Raglai,&nbsp;ở x&atilde; Ma Nới, huyện Ninh Sơn, tỉnh Ninh Thuận,&nbsp;bị dị tật bẩm sinh sứt m&ocirc;i hở v&ograve;m khi ch&agrave;o đời c&aacute;ch đ&acirc;y 16 năm. Họ&nbsp;h&agrave;ng thấy b&eacute; mặt mũi gớm ghiếc kh&aacute;c thường như quỷ n&ecirc;n muốn &quot;để cho chết&quot; theo hủ tục người d&acirc;n tộc. Người mẹ&nbsp;ki&ecirc;n quyết giữ lại v&igrave;&nbsp;&quot;trời cho sao để vậy&quot;.&nbsp;Mặc cảm tật nguyền, cậu b&eacute; kh&ocirc;ng đến trường, kh&ocirc;ng c&oacute; bạn b&egrave;, lớn l&ecirc;n chỉ suốt ng&agrave;y l&ecirc;n rẫy.</p>\r\n\r\n<p>Ng&agrave;y 8/8 cậu b&eacute; Rẫy được nhiếp ảnh gia Trần Trọng Lượm đưa v&agrave;o Bệnh viện Nhi đồng 1 TP HCM điều trị hở h&agrave;m ếch.&nbsp;Năm 2014, anh Lượm t&igrave;nh cờ đến Ma Nới gặp v&agrave; chụp ảnh cậu b&eacute; hở h&agrave;m ếch Tạ Y&ecirc;n Nghiệp. Bức ảnh được anh đặt t&ecirc;n l&agrave;&nbsp;<a href="https://suckhoe.vnexpress.net/tin-tuc/cac-benh/nu-cuoi-cua-mot-dua-tre-lay-dong-trieu-trai-tim-3109353.html">Đằng sau nụ cười</a>&nbsp;v&agrave; gi&agrave;nh giải đặc biệt Cuộc thi Ảnh nghệ thuật VnExpress 2014. Nhờ bức ảnh n&agrave;y, cậu b&eacute; được cộng đồng biết đến v&agrave; nhiều nh&agrave; hảo t&acirc;m gi&uacute;p đưa về TP HCM&nbsp;<a href="https://suckhoe.vnexpress.net/photo/cac-benh/ha-nh-tri-nh-thay-do-i-ky--die-u-cu-a-ca-u-be--da-ng-sau-nu--cuo-i-3155739.html">phẫu thuật v&aacute; m&ocirc;i</a>. Lần n&agrave;y trở lại Ma Nới thăm b&eacute; Nghiệp, anh Lượm gặp b&eacute; Rẫy v&agrave; quyết định nối kết&nbsp;tổ chức Smile Train&nbsp;đưa cậu thiếu ni&ecirc;n v&agrave;o TP HCM chữa trị.&nbsp;</p>\r\n', 2, 1, '2017-12-16 22:35:46', 'Suc Khoe', 'hinhanh/2017/12/Suc Khoe/suckhoe5.jpg'),
(50, 'Triều Nguyễn dẹp lấn chiếm đường phố bằng cách nào?', '<h2>Người d&acirc;n, quan lại nếu lấn chiếm đường l&agrave;m nh&agrave; ở hay trồng c&acirc;y đều c&oacute; thể bị phạt đ&aacute;nh, đ&oacute;ng g&ocirc;ng, hạ lương.</h2>\r\n\r\n<p>Sau khi l&ecirc;n ng&ocirc;i, vua Gia Long thiết lập luật ph&aacute;p một c&aacute;ch chặt chẽ, trong đ&oacute; c&oacute; cả luật xử phạt người d&acirc;n lấn chiếm đường phố l&agrave;m nh&agrave; ở hay l&agrave;m vườn trồng c&acirc;y.</p>\r\n\r\n<p>Theo s&aacute;ch&nbsp;<em>Kh&acirc;m định Đại nam Hội điển sự lệ</em>, vua Gia Long năm thứ 12 đ&atilde; ban sắc dụ, c&aacute;c đường&nbsp;c&ocirc;ng trong kinh th&agrave;nh v&agrave; b&ecirc;n trong, b&ecirc;n ngo&agrave;i ch&acirc;n th&agrave;nh đều kh&ocirc;ng được đ&agrave;o bậy, ai l&agrave;m tr&aacute;i bị xử đ&aacute;nh 30 roi, bắt lắp phẳng lại. Xung quanh ngo&agrave;i kinh th&agrave;nh, từ ch&acirc;n th&agrave;nh bao ngo&agrave;i đến ch&acirc;n th&agrave;nh trong, người n&agrave;o thả r&ocirc;ng tr&acirc;u b&ograve; ăn cỏ th&igrave; bị xử đ&aacute;nh 30 roi, nếu để tr&acirc;u b&ograve; chạy l&ecirc;n cửa th&agrave;nh người chăn bị xử đ&aacute;nh 50 roi. Người l&iacute;nh coi giữ th&agrave;nh bị phạt 30 roi.&nbsp;</p>\r\n\r\n<p>Người d&acirc;n nếu để tr&acirc;u b&ograve; chạy v&agrave;o trong th&agrave;nh, qu&acirc;n l&iacute;nh n&agrave;o bắt được sẽ đem mổ thịt. Người ấy sẽ bị xử tội, sung v&agrave;o l&agrave;m hỏa đầu ở vệ qu&acirc;n. Vi&ecirc;n chức qu&acirc;n l&iacute;nh coi giữ địa phận th&agrave;nh bao ngo&agrave;i v&agrave; giữ cửa th&agrave;nh, đều xử đ&aacute;nh 50 roi.</p>\r\n\r\n<p>Vua Gia Long năm thứ 17 ban chỉ dụ quy định, bốn mặt ngo&agrave;i kinh th&agrave;nh, từ ch&acirc;n th&agrave;nh bao ngo&agrave;i cho đến ch&acirc;n th&agrave;nh trong, kh&ocirc;ng được dựng l&agrave;m liều qu&aacute;n. Ai vi phạm th&igrave; xử đ&aacute;nh 50 roi, bắt phải dỡ bỏ đi. Ri&ecirc;ng c&oacute; c&aacute;c qu&acirc;n, d&acirc;n n&agrave;o canh giữ c&aacute;c cầu th&igrave; cho ph&eacute;p l&agrave;m một c&aacute;i lều nhỏ gần đấy để tiện việc canh giữ.</p>\r\n\r\n<p>Đến thời Minh Mạng, nh&agrave; vua quy định rất r&otilde; việc xử phạt những người cố t&igrave;nh lấn chiếm đường, h&egrave; phố.</p>\r\n\r\n<p>V&agrave;o năm thứ 8 trị vị, trước thực trạng&nbsp;b&ecirc;n trong kinh th&agrave;nh, c&aacute;c phủ đệ v&agrave; dinh thự c&aacute;c sở, ở đường phố b&ecirc;n ngo&agrave;i tường bao quanh, c&oacute; nhiều người thuộc vi&ecirc;n v&agrave; qu&acirc;n d&acirc;n dựng l&agrave;m nh&agrave; tranh b&aacute;m v&agrave;o xung quanh tường để ở rất hỗn tạp, vua Minh Mạng đ&atilde; ban chỉ dụ giao cho đề đốc kinh th&agrave;nh v&agrave; nha m&ocirc;n phủ Thừa Thi&ecirc;n đến tra x&eacute;t bắt dỡ bỏ đi.</p>\r\n\r\n<p>Sau đ&oacute;, vua quy định, người n&agrave;o c&ograve;n d&aacute;m l&agrave;m nh&agrave; hỗn tạp như thế nữa sẽ bị xử phạt 100 trượng, đ&oacute;ng g&ocirc;ng b&ecirc;u một th&aacute;ng. Phủ đệ, dinh thự, sở n&agrave;o m&agrave; kh&ocirc;ng biết cấm ngăn, nếu l&agrave; ho&agrave;ng th&acirc;n th&igrave; bắt tội vi&ecirc;n trưởng sư, c&ograve;n c&aacute;c quan vi&ecirc;n kh&aacute;c th&igrave; xử giảm ba bậc.</p>\r\n\r\n<p>Vi&ecirc;n đề đốc v&agrave; phủ Thừa Thi&ecirc;n kh&ocirc;ng biết kh&aacute;m x&eacute;t ra m&agrave; thi h&agrave;nh th&igrave; vi&ecirc;n dịch n&agrave;o đi tra kh&aacute;m phải xử phạt 80 trượng. Vi&ecirc;n đề đốc v&agrave; phủ do&atilde;n, phủ thừa đều phải phạt lương 6 th&aacute;ng.</p>\r\n', 4, 1, '2017-12-16 22:51:40', 'Phap Luat', 'hinhanh/2017/12/Phap Luat/phapluat5.jpg'),
(49, 'Tranh luận về đề xuất loại tác phẩm Chí Phèo khỏi sách giáo khoa', '<h2>Theo nghi&ecirc;n cứu sinh S&oacute;ng Hiền (ĐH Newcastle, Australia), t&aacute;c phẩm Ch&iacute; Ph&egrave;o c&oacute; thể t&aacute;c động ti&ecirc;u cực tới học sinh n&ecirc;n đề xuất bỏ khỏi s&aacute;ch gi&aacute;o khoa.</h2>\r\n\r\n<p>Đề xuất của nghi&ecirc;n cứu sinh Nguyễn S&oacute;ng Hiền&nbsp;(Đại học Newcastle, Australia) về việc loại t&aacute;c phẩm&nbsp;<em>Ch&iacute; Ph&egrave;o</em>&nbsp;của nh&agrave; văn Nam Cao khỏi chương tr&igrave;nh s&aacute;ch gi&aacute;o khoa Ngữ văn lớp 11, tạo ra cuộc tranh luận s&ocirc;i nổi trong dư luận.&nbsp;</p>\r\n\r\n<p><strong>&#39;Ch&iacute; Ph&egrave;o c&oacute; thể t&aacute;c động ti&ecirc;u cực đến nhận thức của người học&#39;&nbsp;</strong></p>\r\n\r\n<p>Anh S&oacute;ng Hiền cho rằng,&nbsp;ở g&oacute;c độ gi&aacute;o dục, truyện ngắn&nbsp;<em>Ch&iacute; Ph&egrave;o</em>&nbsp;c&oacute; thể&nbsp;t&aacute;c động ti&ecirc;u cực đến nhận thức của học sinh đang ở tuổi chưa ho&agrave;n thiện về mặt nhận thức x&atilde; hội. Trẻ vị th&agrave;nh ni&ecirc;n c&oacute; sự ph&aacute;t&nbsp;triển t&acirc;m l&yacute; kh&aacute; phức tạp, th&iacute;ch nổi loạn, th&iacute;ch khẳng định c&aacute;i t&ocirc;i n&ecirc;n dễ bị ti&ecirc;m nhiễm c&aacute;i xấu.&nbsp;</p>\r\n\r\n<p>Trong khi đ&oacute;, t&aacute;c phẩm&nbsp;<em>Ch&iacute; Ph&egrave;o</em>&nbsp;kể về &quot;con quỷ của l&agrave;ng Vũ Đại&quot; suốt ng&agrave;y chỉ biết uống rượu, rạch mặt ăn vạ, xin đểu, đốt qu&aacute;n, thậm ch&iacute; cưỡng hiếp (với Thị Nở), giết người (B&aacute; Kiến)... nhưng vẫn được cho rằng đ&aacute;ng thương, đ&aacute;ng cảm th&ocirc;ng v&igrave; xuất ph&aacute;t l&agrave; n&ocirc;ng d&acirc;n hiền l&agrave;nh, bị x&atilde; hội phong kiến lưu manh h&oacute;a.&nbsp;</p>\r\n\r\n<p>&quot;Việc giết B&aacute; Kiến sau khi uống rượu say cũng l&agrave; một h&agrave;nh động kh&ocirc;ng thể dung thứ, cho d&ugrave; nhiều học giả v&agrave; nh&agrave; ph&ecirc; b&igrave;nh h&igrave;nh tượng h&oacute;a n&oacute; l&agrave; sự phản kh&aacute;ng của tầng lớp bần n&ocirc;ng đối với giai cấp cường h&agrave;o, &aacute;c b&aacute;.&nbsp;Nhưng xin thưa, đ&oacute; l&agrave; sự quy chụp v&agrave; &aacute;p đặt khi&ecirc;n cưỡng. Ch&iacute; đ&atilde; giết người trong l&uacute;c say, đ&oacute; l&agrave; h&agrave;nh vi kh&ocirc;ng phải của một con người. Cho d&ugrave; ở bất kỳ x&atilde; hội n&agrave;o, những h&agrave;nh động đ&oacute; đều đ&aacute;ng bị l&ecirc;n &aacute;n v&agrave; c&aacute;ch ly ra khỏi đời sống x&atilde; hội&quot;, anh Hiền ph&acirc;n t&iacute;ch.</p>\r\n\r\n<p>Nghi&ecirc;n cứu sinh&nbsp;ng&agrave;nh gi&aacute;o dục v&agrave; nghệ thuật&nbsp;đồng thời chỉ ra việc Ch&iacute; uống rượu say rồi cưỡng bức Thị Nở l&agrave; h&agrave;nh vi phạm ph&aacute;p, đ&aacute;ng l&ecirc;n &aacute;n, nhưng lại được nhiều nh&agrave; ph&ecirc; b&igrave;nh, học giả xem đ&oacute; l&agrave; sự thức tỉnh của t&iacute;nh thiện trong con người Ch&iacute;.</p>\r\n\r\n<p>&quot;Ch&uacute;ng ta đ&acirc;u d&aacute;m chắc được rằng c&aacute;c gi&aacute;o vi&ecirc;n liệu c&oacute; đủ thời gian để truyền tải hết gi&aacute; trị nh&acirc;n văn của t&aacute;c phẩm khi ch&iacute;nh t&aacute;c phẩm đ&oacute; kh&ocirc;ng được dạy trong một chỉnh thể đầy đủ (bản in trong s&aacute;ch gi&aacute;o khoa hiện đ&atilde; lược đoạn viết về cảnh &acirc;n &aacute;i của Ch&iacute; Ph&egrave;o với Thị Nở). Cũng&nbsp;đ&acirc;u ai d&aacute;m chắc được rằng tất cả học sinh c&oacute; thể nhận thức được c&aacute;i hay của t&aacute;c phẩm, hay chỉ nh&igrave;n v&agrave;o những c&aacute;i xấu của nh&acirc;n vật Ch&iacute; để bắt chước&quot;, anh Hiền n&oacute;i.&nbsp;</p>\r\n\r\n<p>Anh Hiền quan niệm, gi&aacute;o dục phải hướng tới hạn chế tối thiểu những mặt tr&aacute;i, t&aacute;c động ti&ecirc;u cực đối với trẻ em. Do đ&oacute;, khi&nbsp;đưa bất kỳ kiến thức, nội dung hay chương tr&igrave;nh n&agrave;o v&agrave;o giảng dạy, nh&agrave; quản l&yacute;, gi&aacute;o vi&ecirc;n cần nh&igrave;n thấu đ&aacute;o, to&agrave;n diện, xem n&oacute; lợi &iacute;ch hay t&aacute;c hại v&agrave; ph&ugrave; hợp như thế n&agrave;o với y&ecirc;u cầu của thực&nbsp;tế cuộc sống. &quot;Đừng bao giờ v&igrave; gi&aacute; trị h&agrave;n l&acirc;m của kiến thức m&agrave; bỏ qu&ecirc;n v&agrave; xem nhẹ những gi&aacute; trị gi&aacute;o dục đối với học sinh&quot;, anh Hiền n&oacute;i.</p>\r\n', 3, 1, '2017-12-16 22:48:43', 'Giao Duc', 'hinhanh/2017/12/Giao Duc/giaoduc11.jpg'),
(48, 'Đề xuất lương đặc thù cho giáo viên', '<h2>Giáo dục đào tạo con người, giáo viên dạy các cấp có yêu cầu trình độ khác nhau nên chuyên gia cho rằng họ cần được hưởng lương đặc thù.</h2>\r\n<p>Hội thảo lấy ý kiến chuyên gia về việc sửa đổi, bổ sung một số điều của Luật giáo dục, Luật Giáo dục đại học do Ủy ban Văn hóa Giáo dục thanh thiếu niên và nhi đồng tổ chức ngày 15/12.</p>\r\n\r\n<p>Giáo sư Đào Trọng Thi (nguyên Chủ nhiệm Ủy ban Văn hóa giáo dục thanh thiếu niên và nhi đồng) đồng tình với chủ trương nâng lương cho giáo viên, vì nghề giáo đặc biệt quan trọng, đào tạo ra con người, có ảnh hưởng lớn đến tương lai đất nước. Công việc của những người thầy vất vả, áp lực từ nhiều phía: học sinh, gia đình, nhà trường... Thu nhập của họ phải xứng đáng với vị trí và trách nhiệm được giao. </p>\r\n\r\n<p>Quy định lương nhà giáo được xếp cao nhất trong hệ thống thang bậc lương hành chính sự nghiệp, theo ông Thi đã có từ hội nghị Trung ương năm 1996. Tuy nhiên, sau 20 năm điều này vẫn chưa được thực hiện do chưa được luật hóa.</p>\r\n\r\n<p>"Hiện nay giáo viên được hưởng thêm phụ cấp giảng dạy và thâm niên, nhưng đây chỉ là yếu tố cứu trợ cho mức lương đang quá thấp, không mang lại cho giáo viên niềm tự hào và sự ổn định về thu nhập", ông Thi nói. Khi cơ chế giúp nhà giáo có được thu nhập tốt, họ sẽ duy trì được đam mê, trách nhiệm với công việc và đào tạo ra những học sinh, sinh viên tốt.</p>\r\n\r\n<p>Trong khi các ngành khác chỉ yêu cầu một trình độ cho nhiều vị trí công tác ví dụ chuyên viên, bộ trưởng đều có thể chung trình độ đại học, ngành giáo dục lại đặt ra yêu cầu trình độ riêng cho giáo viên dạy các cấp. Ví dụ, giáo viên mầm non phải có bằng từ trung cấp trở lên, giáo viên dạy THPT phải có bằng đại học, để trở thành giảng viên dạy các môn lý thuyết của chương trình đào tạo đại học phải có bằng thạc sĩ, người có bằng tiến sĩ mới được làm giảng viên giảng dạy và hướng dẫn chuyên đề, luận văn, luận án...</p>\r\n\r\n<p>Từ thực tế này, GS Thi đề xuất "phải có thang bảng lương đặc thù riêng cho ngành giáo dục. Lương nhà giáo phải gắn liền với trình độ đào tạo của họ". Đây cũng là ý kiến được một số đại biểu đồng tình.</p>\r\n\r\n<p><strong>Khó thực hiện việc "không phân biệt bằng đại học tại chức và chính quy" </strong></p>\r\n\r\n<p>Trong phiên họp về sửa đổi bổ sung một số điều của Luật Giáo dục đại học, góp ý của PGS Trần Văn Tớp (Hiệu phó Đại học Bách khoa Hà Nội) về những bất cập nếu áp dụng việc không phân biệt bằng đại học hệ tại chức và chính quy, nhận được chú ý. Đề xuất nêu trong Luật sửa đổi theo ông khó khả thi ở thời điểm hiện tại. Nó chỉ có thể thực hiện khi mọi quy trình và chất lượng đào tạo phải giống nhau từ khâu tuyển sinh đầu vào, quản lý đào tạo, chương trình đào tạo, chuẩn đầu ra, cách thức thi cử, đánh giá...</p>\r\n', 3, 1, '2017-12-16 22:44:12', 'Giao Duc', 'hinhanh/2017/12/Giao Duc/giaoduc2.jpg'),
(39, 'Chiều nay tư vấn trực tuyến tiêm văcxin an toàn, tránh tai biến cho trẻ', '<h2>14h ng&agrave;y 22/12, Đại diện Cục Y tế Dự ph&ograve;ng v&agrave; Viện Vệ sinh Dịch tễ Trung ương sẽ tư vấn cho cha mẹ những lưu &yacute; trước v&agrave; sau khi đưa b&eacute; đi ti&ecirc;m chủng.</h2>\r\n\r\n<p>Theo Tiến sĩ Nguyễn Xu&acirc;n T&ugrave;ng - Trưởng ph&ograve;ng quản l&yacute; văcxin v&agrave; x&eacute;t nghiệm (Cục Y tế Dự ph&ograve;ng), hiện c&oacute; gần 30 bệnh nhiễm tr&ugrave;ng c&oacute; thể dự ph&ograve;ng được bằng văcxin. Phần lớn l&agrave; c&aacute;c bệnh thường gặp, biến chứng nguy hiểm, nguy cơ tử vong cao. Ngo&agrave;i việc giảm tỷ lệ mắc v&agrave; tử vong do bệnh nhiễm tr&ugrave;ng, văcxin c&ograve;n gi&uacute;p trẻ tăng sức đề kh&aacute;ng, giảm số ng&agrave;y ốm v&agrave; nhập viện cũng như thời gian, c&ocirc;ng sức, chi ph&iacute; chăm s&oacute;c y tế cho cha mẹ.</p>\r\n\r\n<p>Văcxin ti&ecirc;m chủng mở rộng được Trung t&acirc;m Dự ph&ograve;ng v&agrave; Kiểm so&aacute;t Bệnh tật Mỹ (CDC) xếp thứ 4 trong 10 th&agrave;nh tựu y tế c&ocirc;ng lớn nhất thế kỷ 20. Nhờ c&oacute; văcxin m&agrave; thế giới thanh to&aacute;n được bệnh đậu m&ugrave;a, bại liệt. H&agrave;ng năm giảm 2,5 triệu trẻ chết v&igrave; vi&ecirc;m gan B, vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o do Hib. Ngo&agrave;i ra, c&ograve;n nhiều loại văcxin mới ph&ograve;ng vi&ecirc;m m&agrave;ng n&atilde;o m&ocirc; cầu, vi&ecirc;m phổi do phế cầu khuẩn, ti&ecirc;u chảy do rota virus...</p>\r\n\r\n<p>Tuy nhi&ecirc;n, trẻ c&oacute; thể gặp một v&agrave;i phản ứng sau ti&ecirc;m chủng li&ecirc;n quan đến văcxin, hoặc thao t&aacute;c của nh&acirc;n vi&ecirc;n y tế. Hầu hết c&aacute;c phản ứng nhẹ như&nbsp;kh&oacute; chịu,&nbsp;ch&aacute;n ăn, ti&ecirc;u chảy, sưng đau tại chỗ sẽ tự khỏi. Mũi lao (BCG) c&ograve;n l&agrave;m xuất hiện nốt sẩn đỏ tại chỗ ti&ecirc;m, nhiều tuần kế tiếp sẽ mưng mủ, sau v&agrave;i th&aacute;ng th&igrave; l&agrave;nh v&agrave; để lại sẹo.&nbsp;</p>\r\n\r\n<p>Văcxin sống giảm độc lực như sởi MMR v&agrave; OPV c&oacute; thể g&acirc;y sốt, ph&aacute;t ban hoặc vi&ecirc;m kết mạc ở 5-15% trẻ ti&ecirc;m. Với văcxin 5 trong một (bạch hầu, ho g&agrave;, uốn v&aacute;n, vi&ecirc;m gan B v&agrave; vi&ecirc;m m&agrave;ng n&atilde;o mủ Hib), khoảng 10% trẻ sốt hơn 38 độ C, số &iacute;t sốt cao g&acirc;y co giật hoặc sốc phản vệ...</p>\r\n\r\n<p>Chuy&ecirc;n gia khuy&ecirc;n cha mẹ n&ecirc;n ti&ecirc;m ph&ograve;ng đ&uacute;ng lịch cho b&eacute;, nhằm tăng hiệu quả của văcxin. Để hạn chế phản ứng phụ, cần xem x&eacute;t thể trạng của b&eacute; trước ti&ecirc;m, kiểm tra hạn sử dụng của văcxin. Sau ti&ecirc;m, cần ngồi chờ tại nơi ti&ecirc;m chủng &iacute;t nhất 30 ph&uacute;t v&agrave; theo d&otilde;i sức khỏe b&eacute; khi về nh&agrave;.</p>\r\n\r\n<p>Khi n&agrave;o trẻ kh&ocirc;ng n&ecirc;n đi ti&ecirc;m, ti&ecirc;m văcxin buổi s&aacute;ng hay chiều tốt hơn, phản ứng c&oacute; thể gặp phải của từng loại văcxin, c&aacute;ch sơ cứu khi trẻ tai biến... sẽ được chuy&ecirc;n gia tư vấn l&uacute;c 14h ng&agrave;y 22/12 tr&ecirc;n&nbsp;<em>VnExpress</em>. Chuy&ecirc;n gia gồm Tiến sĩ Nguyễn Xu&acirc;n T&ugrave;ng - Trưởng ph&ograve;ng quản l&yacute; văcxin v&agrave; x&eacute;t nghiệm (Cục Y tế Dự ph&ograve;ng); Thạc sĩ Trần Thị Lan Anh - chuy&ecirc;n vi&ecirc;n Văn ph&ograve;ng ti&ecirc;m chủng mở rộng miền Bắc (Viện Vệ sinh Dịch tễ Trung ương).</p>\r\n', 1, 1, '2017-12-16 22:22:34', 'Suc Khoe', 'hinhanh/2017/12/Suc Khoe/suckhoe3.jpg'),
(37, 'Bác sĩ lưu ý cách chăm sóc và điều trị trẻ mắc bệnh hô hấp', '<h2>B&aacute;c sĩ Nguyễn Thị Diệu Th&uacute;y khuy&ecirc;n cha mẹ tr&aacute;nh lạm dụng thuốc cắt cơn ho, thay v&agrave;o đ&oacute; d&ugrave;ng thảo dược tống đờm ra, gi&uacute;p trẻ nhanh khỏi hơn.</h2>\r\n\r\n<p>Ph&oacute; gi&aacute;o sư, Tiến sĩ Nguyễn Thị Diệu Th&uacute;y - Ph&oacute; trưởng khoa miễn dịch dị ứng, Bệnh viện Nhi Trung ương cho biết, trung b&igrave;nh mỗi ng&agrave;y, bệnh viện tiếp nhận khoảng 3.000 trẻ. Trong đ&oacute;, 40-50%&nbsp;bệnh nhi đến kh&aacute;m h&ocirc; hấp; thời điểm hiện tại, con số l&ecirc;n tới 70-80%.</p>\r\n\r\n<p><em>- Tại sao bệnh đường h&ocirc; hấp b&ugrave;ng ph&aacute;t mạnh trong thời điểm n&agrave;y, thưa b&aacute;c sĩ?</em></p>\r\n\r\n<p>- M&ugrave;a lạnh l&agrave; thời điểm gia tăng c&aacute;c bệnh về h&ocirc; hấp do điều kiện thời tiết, nhiệt độ li&ecirc;n tục thay đổi. Vi khuẩn v&agrave; virus g&acirc;y bệnh mạnh l&ecirc;n, trong khi sức đề kh&aacute;ng của trẻ c&ograve;n non yếu so với người lớn.</p>\r\n\r\n<p>Virus v&agrave; vi khuẩn lu&ocirc;n tồn tại sẵn trong cơ thể con người. Khi điều kiện th&iacute;ch hợp, ch&uacute;ng sẽ b&ugrave;ng ph&aacute;t tấn c&ocirc;ng cơ thể, đường h&ocirc; hấp thường l&agrave; cơ quan chịu trận đầu ti&ecirc;n.</p>\r\n\r\n<p><em>- Những bệnh vi&ecirc;m đường h&ocirc; hấp n&agrave;o trẻ nhỏ thường gặp nhất?</em></p>\r\n\r\n<p>-&nbsp;Hắt hơi, sổ mũi, ho&hellip; l&agrave; những bệnh l&yacute; vi&ecirc;m đường h&ocirc; hấp tr&ecirc;n ở thể nhẹ. Ở thể nặng, bệnh lan xuống đường h&ocirc; hấp dưới th&agrave;nh vi&ecirc;m phế quản, tiểu phế quản, phổi, xoang&hellip;</p>\r\n\r\n<p><em>- Nguy&ecirc;n nh&acirc;n g&acirc;y bệnh chủ yếu l&agrave; g&igrave;?</em></p>\r\n\r\n<p>- Chủ yếu do virus v&agrave; vi khuẩn g&acirc;y vi&ecirc;m nhiễm đường h&ocirc; hấp. Một số trường hợp do nấm, k&yacute; sinh tr&ugrave;ng. Trẻ c&oacute; cơ địa dị ứng, nhạy cảm với thay đổi thời tiết, c&agrave;ng dễ mắc bệnh hơn.</p>\r\n\r\n<p>Virus chiếm hơn 90% nguy&ecirc;n nh&acirc;n khởi ph&aacute;t g&acirc;y bệnh vi&ecirc;m đường h&ocirc; hấp. Sau đ&oacute;, trẻ c&oacute; thể bội nhiễm th&ecirc;m vi khuẩn, nếu kh&ocirc;ng c&oacute; biện ph&aacute;p chăm s&oacute;c v&agrave; điều trị đ&uacute;ng đắn v&agrave; kịp thời. Ngo&agrave;i ra, b&eacute; c&oacute; thể nhiễm tr&ugrave;ng đường h&ocirc; hấp thứ ph&aacute;t do chấn thương, tai nạn&hellip;</p>\r\n\r\n<p><em>- Phụ huynh thường mắc phải sai lầm n&agrave;o khi chăm s&oacute;c trẻ mắc bệnh h&ocirc; hấp?</em></p>\r\n\r\n<p>- Sai lầm phổ biến của bố mẹ l&agrave; tự &yacute; mua thuốc cho b&eacute;, lạm dụng kh&aacute;ng sinh, hoặc d&ugrave;ng lại đơn thuốc cũ. Một số người đi t&igrave;m lời khuy&ecirc;n từ người th&acirc;n, đồng nghiệp, h&agrave;ng x&oacute;m&hellip; thiếu chuy&ecirc;n m&ocirc;n y tế. Kh&ocirc;ng &iacute;t phụ huynh &aacute;p dụng&nbsp; kinh nghiệm d&acirc;n gian chữa bệnh cho b&eacute;.</p>\r\n\r\n<p>Bố mẹ n&ecirc;n đưa trẻ đến thăm kh&aacute;m b&aacute;c sĩ c&oacute; chuy&ecirc;n m&ocirc;n. Ngo&agrave;i ra, cần thường xuy&ecirc;n theo d&otilde;i diễn biến bệnh của trẻ, để c&oacute; biện ph&aacute;p xử k&yacute; kịp thời v&agrave; đ&uacute;ng đắn.</p>\r\n\r\n<p><em>- Cha mẹ n&ecirc;n xử tr&iacute; sao khi trẻ ho nhiều?</em></p>\r\n\r\n<p>- C&aacute;c b&eacute; mắc bệnh đường h&ocirc; hấp thường ho nhiều v&agrave; k&eacute;o d&agrave;i, khiến cha mẹ lo lắng, t&igrave;m c&aacute;ch cắt cơn ho của b&eacute; c&agrave;ng nhanh c&agrave;ng tốt. Tuy nhi&ecirc;n, phụ huynh cần biết rằng ho l&agrave; phản xạ tự nhi&ecirc;n v&agrave; c&oacute; lợi, gi&uacute;p cơ thể tự bảo vệ đường thở, đường lưu th&ocirc;ng kh&ocirc;ng kh&iacute; ra v&agrave;o phổi. Ho cũng gi&uacute;p loại bỏ c&aacute;c vật thể cản trở hoặc đờm nhầy trong đường h&ocirc; hấp ra ngo&agrave;i.</p>\r\n\r\n<p>V&igrave; thế, thay v&igrave; l&agrave;m mất phản xạ ho của trẻ bằng mọi c&aacute;ch, cha mẹ n&ecirc;n d&ugrave;ng thuốc thảo dược l&agrave;m lo&atilde;ng đờm, giảm đau r&aacute;t cổ họng, gi&uacute;p tống đờm ra nhiều hơn. Trẻ dễ chịu, dễ thở hơn, từ đ&oacute; phản xạ ho sẽ tự giảm xuống cho đến khi hết hẳn.</p>\r\n\r\n<p><em>- Bố mẹ n&ecirc;n ph&ograve;ng ngừa bệnh đường h&ocirc; hấp cho trẻ như thế n&agrave;o?</em></p>\r\n\r\n<p>- C&oacute; tới 70% bệnh h&ocirc; hấp khởi ph&aacute;t do thời tiết lạnh. V&igrave; thế, khi ra đường, c&aacute;c bố mẹ n&ecirc;n ch&uacute; &yacute; mặc đủ &aacute;o, đeo khẩu trang, giữ ấm cơ thể cho b&eacute;. Bố mẹ cũng n&ecirc;n bổ sung dinh dưỡng; cho b&eacute; uống nhiều nước, đặc biệt l&agrave; nước ấm; bổ sung hoa quả để tăng cường sức đề kh&aacute;ng.</p>\r\n\r\n<p>Phụ huynh cần vệ sinh sạch sẽ mũi, họng, gi&uacute;p đường h&ocirc; hấp th&ocirc;ng tho&aacute;ng hơn. Ngo&agrave;i ra, n&ecirc;n hạn chế cho b&eacute; đến chỗ đ&ocirc;ng người, c&aacute;ch ly với những người mắc bệnh h&ocirc; hấp, để tr&aacute;nh l&acirc;y lan virus, vi khuẩn.</p>\r\n\r\n<p>Khi mới xuất hiện c&aacute;c triệu chứng ban đầu của vi&ecirc;m nhiễm đường h&ocirc; hấp như sổ mũi, ho, sốt nhẹ&hellip;, c&oacute; thể d&ugrave;ng c&aacute;c loại thuốc thảo dược gi&uacute;p ti&ecirc;u nhầy, lo&atilde;ng đờm, chống co thắt phế quản. Với trẻ sơ sinh, cần lựa chọn thuốc ho c&oacute; chỉ định r&otilde; r&agrave;ng cho đối tượng n&agrave;y, v&igrave; b&eacute; rất nhạy cảm với c&aacute;c hoạt chất.</p>\r\n\r\n<p>Khi b&ecirc;nh c&oacute; triệu chứng nặng hơn như ho l&acirc;u ng&agrave;y, sốt&hellip;, n&ecirc;n đưa b&eacute; đến c&aacute;c cơ sở y tế để được b&aacute;c sĩ thăm kh&aacute;m v&agrave; điều trị.</p>\r\n', 1, 1, '2017-12-16 22:16:31', 'Suc Khoe', 'hinhanh/2017/12/Suc Khoe/suckhoe4.jpg');
INSERT INTO `posts` (`id`, `title`, `content`, `user_id`, `is_public`, `createdate`, `theme`, `url_hinhanh`) VALUES
(38, 'Chiều nay tư vấn trực tuyến dinh dưỡng cho người từ 50 tuổi', '<h2>14h30 chiều nay, Tiến sĩ, b&aacute;c sĩ Lưu Ng&acirc;n T&acirc;m - Trưởng khoa dinh dưỡng, Bệnh viện Chợ Rẫy v&agrave; Nghệ sĩ nh&acirc;n d&acirc;n L&ecirc; Khanh sẽ tư vấn cho độc giả những b&iacute; quyết dinh dưỡng để tăng cường sức khỏe v&agrave; thực hiện ước mơ sau tuổi trung ni&ecirc;n.</h2>\r\n\r\n<p>&quot;Gi&agrave; b&aacute;t canh, trẻ manh &aacute;o mới&quot;, c&acirc;u n&oacute;i của người xưa &yacute; chỉ mong muốn của người c&oacute; tuổi được con ch&aacute;u nấu m&oacute;n ngon dễ nuốt, b&aacute;o đ&aacute;p c&ocirc;ng ơn sinh th&agrave;nh dưỡng dục. Bởi khi bước v&agrave;o tuổi 50, ai cũng gặp vấn đề ch&aacute;n ăn, ăn kh&ocirc;ng ngon miệng, hấp thu k&eacute;m, rối loạn chuyển h&oacute;a...</p>\r\n\r\n<p>Qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a ảnh hưởng đến mọi cơ quan trong cơ thể, đặc biệt l&agrave; hệ ti&ecirc;u h&oacute;a. 70% hệ miễn dịch của cơ thể nằm ở đường ruột. Trục trặc hệ ti&ecirc;u h&oacute;a khiến sức khỏe người từ 50 tuổi suy giảm, dễ mệt mỏi, cảm c&uacute;m khi thời tiết thay đổi, cơ xương yếu, huyết &aacute;p bất thường...</p>\r\n\r\n<p>Suy dinh dưỡng cũng l&agrave; vấn đề người c&oacute; tuổi phải đối mặt. Theo một nghi&ecirc;n cứu đăng tr&ecirc;n Tạp ch&iacute;&nbsp;<em>Clinical Gerontology Geriatrics</em>&nbsp;năm 2015, tiến h&agrave;nh từ năm 2005 đến 2012, tỷ lệ người c&oacute; tuổi suy dinh dưỡng tại khu vực ch&acirc;u &Aacute; ở mức cao. Nh&oacute;m nghi&ecirc;n cứu cũng thực hiện thống k&ecirc; tr&ecirc;n 438 bệnh nh&acirc;n tại Việt Nam, kết quả cho thấy c&oacute; 56% bị suy dinh dưỡng v&agrave; 78% đối mặt với nguy cơ n&agrave;y, cao hơn nhiều quốc gia kh&aacute;c trong khu vực. Dinh dưỡng thiếu hụt khiến sức đề kh&aacute;ng giảm s&uacute;t, người gi&agrave; dễ mắc v&agrave; kh&oacute; chống đỡ với bệnh tật hơn.</p>\r\n\r\n<p>Thế nhưng, người c&oacute; tuổi lại chủ quan việc bổ sung dinh dưỡng hoặc mắc nhiều sai lầm như ăn uống thất thường, ch&aacute;n ăn bỏ bữa, lười uống nước, mua sắm tiết kiệm, lưu trữ thực phẩm cũ. Cũng kh&ocirc;ng &iacute;t người quan niệm sai lầm rằng, gi&agrave; th&igrave; phải ăn &iacute;t để giảm chất b&eacute;o, mỡ m&aacute;u, tr&aacute;nh bệnh tật.</p>\r\n\r\n<p>Theo tiến sĩ, b&aacute;c sĩ Lưu Ng&acirc;n T&acirc;m, trưởng khoa dinh dưỡng, bệnh viện Chợ Rẫy, người tuổi 50 cần được cung cấp đủ chất đạm, giảm chất bột đường, tăng cường chất xơ. Tuy nhi&ecirc;n, chế độ ăn uống c&ograve;n t&ugrave;y thuộc v&agrave;o t&igrave;nh trạng sức khỏe v&agrave; bệnh l&yacute; của từng người. Việc ăn ngon miệng, t&acirc;m l&yacute; thoải m&aacute;i vui vẻ cũng gi&uacute;p người c&oacute; tuổi hấp thụ dinh dưỡng tốt hơn. V&igrave; vậy, người chế biến m&oacute;n ăn n&ecirc;n ch&uacute; &yacute; thay đổi c&aacute;c loại thực phầm v&agrave; đa dạng h&oacute;a bữa ăn h&agrave;ng ng&agrave;y để c&acirc;n bằng dinh dưỡng cho th&agrave;nh vi&ecirc;n tr&ecirc;n 50 tuổi trong gia đ&igrave;nh.</p>\r\n\r\n<p>Với nghệ sĩ nh&acirc;n d&acirc;n L&ecirc; Khanh, dinh dưỡng l&agrave; một trong những yếu tố h&agrave;ng đầu để giữ g&igrave;n sức khỏe, gi&uacute;p cơ thể lu&ocirc;n tr&agrave;n đầy năng lượng.</p>\r\n\r\n<p>Nữ nghệ sĩ coi việc đảm bảo chế độ dinh dưỡng hợp l&yacute; h&agrave;ng ng&agrave;y l&agrave; c&aacute;ch chăm s&oacute;c cho gia đ&igrave;nh, giữ g&igrave;n sức khỏe của bản th&acirc;n v&agrave; c&aacute;c th&agrave;nh vi&ecirc;n. Th&ecirc;m v&agrave;o đ&oacute;, ẩm thực đối với L&ecirc; Khanh kh&ocirc;ng chỉ l&agrave; dinh dưỡng v&agrave; c&ograve;n l&agrave; niềm vui để kh&aacute;m ph&aacute;, c&ugrave;ng chia sẻ với người th&acirc;n v&agrave; l&agrave; c&aacute;ch để l&agrave;m cuộc sống lu&ocirc;n mới mẻ.</p>\r\n\r\n<p>Tiến sĩ, b&aacute;c sĩ Lưu Ng&acirc;n T&acirc;m v&agrave; Nghệ sĩ nh&acirc;n d&acirc;n L&ecirc; Khanh sẽ tư vấn cho độc giả chế độ dinh dưỡng hợp l&yacute; để người từ 50 tuổi sống vui khỏe b&ecirc;n con ch&aacute;u v&agrave;o l&uacute;c 14h30 ng&agrave;y 27/7.</p>\r\n\r\n<p>Tiến sĩ, b&aacute;c sĩ Lưu Ng&acirc;n T&acirc;m hiện l&agrave; Trưởng khoa dinh dưỡng, Bệnh viện Chợ Rẫy. B&agrave; đồng thời l&agrave; s&aacute;ng lập vi&ecirc;n v&agrave; Chủ tịch Hội Dinh dưỡng L&acirc;m s&agrave;ng TP HCM.</p>\r\n\r\n<p>B&aacute;c sĩ Ng&acirc;n tốt nghiệp Đại học Y khoa Phạm Ngọc Thạch năm 1998 v&agrave; c&ocirc;ng t&aacute;c tại khoa dinh dưỡng của Bệnh viện Chợ Rẫy từ năm 1999 tới nay. Trong thời gian đ&oacute;, b&agrave; theo học tại Bệnh viện Charite v&agrave; nhận bằng Tiến sĩ Đại học Y khoa Charite (Berlin, Đức) năm 2009. Nhiều cuốn s&aacute;ch, đề t&agrave;i nghi&ecirc;n cứu của b&agrave; được đ&aacute;nh gi&aacute; cao. Tiến sĩ Ng&acirc;n cũng tham gia đ&agrave;o tạo nhiều b&aacute;c sĩ trẻ về chuy&ecirc;n m&ocirc;n, giảng dạy cho Đại học Y Dược TP HCM, Đại học Y khoa Phạm Ngọc Thạch.</p>\r\n', 1, 1, '2017-12-16 22:19:54', 'Suc Khoe', 'hinhanh/2017/12/Suc Khoe/suckhoe6.jpg'),
(40, 'Thứ trưởng Bộ Y tế Nga thăm bệnh viện mắt quốc tế', '<h2>B&agrave; Khorova Natalya Alexandrovna đến kiểm tra t&igrave;nh h&igrave;nh hoạt động của Bệnh viện Mắt quốc tế Việt - Nga nh&acirc;n chuyến c&ocirc;ng du.</h2>\r\n\r\n<p>Đo&agrave;n l&atilde;nh đạo đến tham quan bệnh viện trong khu&ocirc;n khổ chuyến thăm Việt Nam v&agrave; dự Diễn đ&agrave;n Hợp t&aacute;c Kinh tế Việt - Nga 2017, s&aacute;ng 14/12. Thứ trưởng Bộ Y tế Nga Khorova Natalya Alexandrovna đi c&ugrave;ng b&agrave; Maksima Elena Anatolevna - Vụ trưởng Vụ Hợp t&aacute;c Quốc tế.</p>\r\n\r\n<p>Bệnh viện Mắt Quốc tế Việt - Nga được th&agrave;nh lập theo khu&ocirc;n khổ chương tr&igrave;nh hợp t&aacute;c nh&atilde;n khoa giữa bộ y tế hai nước, k&yacute; kết năm 2007. Đơn vị hiện sử dụng to&agrave;n bộ c&ocirc;ng nghệ y khoa hiện đại của Nga v&agrave; thế giới, kh&aacute;m v&agrave; phẫu thuật trực tiếp bởi đội ngũ chuy&ecirc;n gia nước ngo&agrave;i.</p>\r\n\r\n<p>Đo&agrave;n c&ocirc;ng t&aacute;c đ&atilde; đến thăm hỏi c&aacute;c chuy&ecirc;n gia Nga đang l&agrave;m việc tại đ&acirc;y; tham quan ph&ograve;ng chức năng, hệ thống m&aacute;y m&oacute;c, trang thiết bị đạt chuẩn quốc tế của bệnh viện. Đồng thời, nghe b&aacute;o c&aacute;o tổng quan về quy m&ocirc;, cơ cấu tổ chức, định hướng ph&aacute;t triển v&agrave; c&aacute;c th&agrave;nh t&iacute;ch m&agrave; bệnh viện đạt được trong thời gian qua.</p>\r\n\r\n<p>Sau 10 năm hoạt động, bệnh viện đạt được nhiều&nbsp;kết quả t&iacute;ch cực trong việc hợp t&aacute;c v&agrave; chuyển giao c&ocirc;ng nghệ, mang tới cho người d&acirc;n dịch vụ kh&aacute;m chữa bệnh nh&atilde;n khoa chất lượng cao.</p>\r\n\r\n<p>B&agrave;y tỏ vui mừng trước sự quan t&acirc;m của l&atilde;nh đạo Bộ Y tế Nga, &ocirc;ng Dương Ch&iacute; Ki&ecirc;n - Chủ tịch HĐQT bệnh viện khẳng định sẽ tiếp tục ph&aacute;t huy, tăng cường tiếp thu kỹ thuật v&agrave; c&ocirc;ng nghệ mới. Mục ti&ecirc;u sắp tới sẽ trở th&agrave;nh địa chỉ nh&atilde;n khoa uy t&iacute;n h&agrave;ng đầu tại Việt Nam,&nbsp;gắn kết hơn nữa mối quan hệ hợp t&aacute;c giữa hai nước.</p>\r\n', 1, 1, '2017-12-16 22:24:04', 'Suc Khoe', 'hinhanh/2017/12/Suc Khoe/suckhoe1.jpg'),
(41, 'Dấu hiệu nhận biết viêm tuyến tiền liệt', '<h2>Triệu chứng thường gặp của bệnh l&agrave; tiểu nhiều lần, lắt nhắt, tiểu rớt giọt, tiểu buốt, tiểu đ&ecirc;m, trường hợp nặng c&oacute; thể g&acirc;y b&iacute; tiểu.</h2>\r\n\r\n<p>B&aacute;c sĩ Tr&agrave; Anh Duy cho biết, tuyến tiền liệt l&agrave; một cấu tr&uacute;c đặc trưng chỉ c&oacute; ở nam giới giống như tử cung chỉ c&oacute; ở nữ. Do cấu tr&uacute;c đặc biệt n&ecirc;n tuyến tiền liệt kh&ocirc;ng chỉ tham gia tiết dịch để tạo th&agrave;nh tinh dịch, m&agrave; c&ograve;n c&oacute; c&aacute;c sợi cơ gi&uacute;p cho việc kiểm so&aacute;t hoạt động đi tiểu v&agrave; xuất tinh. Khi tuyến tiền liệt bị vi&ecirc;m nhiễm sẽ ảnh hưởng đến qu&aacute; tr&igrave;nh kiểm so&aacute;t nước tiểu v&agrave; xuất tinh.</p>\r\n\r\n<p>Hầu hết nam giới được chẩn đo&aacute;n vi&ecirc;m tuyến tiền liệt đều c&oacute; cảm gi&aacute;c kh&oacute; chịu trong l&uacute;c tiểu v&agrave; triệu chứng đau tức v&ugrave;ng sau xương mu hoặc tầng sinh m&ocirc;n.</p>\r\n\r\n<p>Theo b&aacute;c sĩ Duy, bệnh n&agrave;y kh&ocirc;ng kh&oacute; chữa, song bệnh nh&acirc;n phải đi kh&aacute;m tại cơ sở nam khoa uy t&iacute;n để được l&agrave;m x&eacute;t nghiệm chẩn đo&aacute;n v&agrave; điều trị triệt để, tr&aacute;nh tự &yacute; d&ugrave;ng thuốc.</p>\r\n\r\n<p>B&aacute;c sĩ khuyến c&aacute;o nam giới khi thấy một dấu hiệu sau n&ecirc;n nghĩ đến bệnh vi&ecirc;m tuyến tiền liệt:</p>\r\n\r\n<p>- Những triệu chứng k&iacute;ch th&iacute;ch đường tiểu như tiểu nhiều lần, lắt nhắt, tiểu rớt giọt, tiểu buốt, tiểu đ&ecirc;m.</p>\r\n\r\n<p>- Những triệu chứng bế tắc như tiểu kh&oacute;, phải rặn, tiểu chậm (đi tiểu nhưng kh&ocirc;ng được ngay). Trường hợp nặng v&agrave; cấp t&iacute;nh c&oacute; thể g&acirc;y b&iacute; tiểu.</p>\r\n\r\n<p>- Triệu chứng đau r&aacute;t v&agrave; tức ở những v&ugrave;ng tr&ecirc;n xương mu, bẹn b&igrave;u, tiểu khung v&agrave; tầng sinh m&ocirc;n.</p>\r\n\r\n<p>- Những rối loạn chức năng giao hợp rất đặc trưng như xuất tinh đau, xuất tinh ra m&aacute;u. Đ&ocirc;i khi, bệnh nh&acirc;n c&oacute; k&egrave;m rối loạn co cứng của dương vật.</p>\r\n\r\n<p>- C&oacute; thể bị sốt cao trong trường hợp vi&ecirc;m cấp t&iacute;nh.</p>\r\n\r\n<p>Những triệu chứng tr&ecirc;n c&oacute; thể gặp ở những bệnh l&yacute; kh&aacute;c của đường tiểu như vi&ecirc;m niệu đạo, vi&ecirc;m b&agrave;ng quang, ph&igrave; đại tuyến tiền liệt&hellip; Do đ&oacute;, để chẩn đo&aacute;n bệnh vi&ecirc;m tuyến tiền liệt cần kết hợp th&ecirc;m c&aacute;c phương ph&aacute;p hỗ trợ như si&ecirc;u &acirc;m, thử kh&aacute;ng nguy&ecirc;n đặc hiệu của tuyến tiền liệt trong m&aacute;u, thử nước tiểu, phết dịch tuyến tiền liệt tiết ra từ thăm kh&aacute;m trực tr&agrave;ng xoa b&oacute;p tuyến tiền liệt&hellip;</p>\r\n\r\n<p>B&aacute;c sĩ lưu &yacute;: Tuyến tiền liệt l&agrave; một tạng nằm s&acirc;u trong v&ugrave;ng tiểu khung n&ecirc;n tần suất bị vi&ecirc;m kh&ocirc;ng cao như mọi người lo lắng. Bệnh l&yacute; n&agrave;y c&oacute; thể gặp ở nam giới trong độ tuổi hoạt động t&igrave;nh dục v&agrave; trung ni&ecirc;n, đặc biệt l&agrave; người quan hệ t&igrave;nh dục kh&ocirc;ng an to&agrave;n. Ngo&agrave;i ra, những yếu tố thuận lợi khiến vi&ecirc;m tuyến tiền liệt xuất hiện như sỏi thận, sỏi b&agrave;ng quang, ph&igrave; đại tuyến tiền liệt, hẹp niệu đạo&hellip;</p>\r\n\r\n<p>Vi&ecirc;m tuyến tiền liệt nếu được chẩn đo&aacute;n sớm v&agrave; ch&iacute;nh x&aacute;c th&igrave; thời gian điều trị tương đối ngắn, chỉ từ 7 đến 10 ng&agrave;y l&agrave; khỏi. To&agrave;n bộ chi ph&iacute; thuốc chỉ v&agrave;i trăm ngh&igrave;n đồng, do vậy bệnh nh&acirc;n kh&ocirc;ng n&ecirc;n lo lắng&nbsp; qu&aacute;.</p>\r\n', 2, 1, '2017-12-16 22:27:33', 'Suc Khoe', 'hinhanh/2017/12/Suc Khoe/suckhoe11.jpg'),
(42, '5 lầm tưởng thường gặp về ung thư tuyến tiền liệt', '<h2>Đa số đ&agrave;n &ocirc;ng cho rằng phẫu thuật ung thư tuyến tiền liệt sẽ kết th&uacute;c đời sống t&igrave;nh dục v&agrave; phải điều trị ngay khi ph&aacute;t hiện bệnh.</h2>\r\n\r\n<p>Dưới đ&acirc;y l&agrave;&nbsp;một số lầm tưởng thường gặp về căn bệnh ung thư n&agrave;y ở đ&agrave;n &ocirc;ng, theo&nbsp;<em>WebMd</em>.</p>\r\n\r\n<p><strong>Phẫu thuật ung thư tuyến&nbsp;tiền liệt nghĩa l&agrave; kết th&uacute;c đời sống t&igrave;nh dục v&agrave; g&acirc;y r&ograve; rỉ nước tiểu</strong></p>\r\n\r\n<p><em>Thực tế:&nbsp;</em>B&aacute;c sĩ phẫu thuật sẽ c&oacute; c&aacute;ch kh&ocirc;ng t&aacute;c động đến c&aacute;c d&acirc;y thần kinh gi&uacute;p duy tr&igrave; qu&aacute; tr&igrave;nh cương. Quan hệ t&igrave;nh dục vẫn diễn ra tốt đẹp sau mổ, song cần 4-24 th&aacute;ng hoặc l&acirc;u hơn để phục hồi. Nam giới trẻ tuổi c&oacute; thời gian l&agrave;nh sớm hơn.&nbsp;Nếu vẫn gặp kh&oacute; khăn, h&atilde;y hỏi b&aacute;c sĩ về c&aacute;c biện ph&aacute;p điều trị rối loạn chức năng cương.</p>\r\n\r\n<p>C&aacute;c phương ph&aacute;p điều trị ung thư tuyến tiền liệt kh&aacute;c như bức xạ v&agrave; liệu ph&aacute;p hormone c&oacute; thể ảnh hưởng đến đời sống t&igrave;nh dục. Trao đổi kỹ với b&aacute;c sĩ để c&oacute; sự lựa chọn ph&ugrave; hợp.</p>\r\n\r\n<p>Bạn c&oacute; thể bị r&ograve; rỉ nước tiểu sau khi phẫu thuật nhưng thường l&agrave; ngắn hạn. Trong v&ograve;ng một năm, khoảng 95% nam giới c&oacute; thể kiểm so&aacute;t b&agrave;ng quang b&igrave;nh thường như trước mổ.</p>\r\n\r\n<p><strong>Chỉ đ&agrave;n &ocirc;ng lớn tuổi mới mắc bệnh ung thư tuyến tiền liệt</strong></p>\r\n\r\n<p><em>Thực tế:&nbsp;</em>Kh&aacute; hiếm nam giới dưới 40 mắc bệnh nhưng kh&ocirc;ng c&oacute; nghĩa l&agrave; kh&ocirc;ng xảy ra. Tuổi t&aacute;c kh&ocirc;ng phải l&agrave; điều duy nhất. C&aacute;c yếu tố nguy cơ bao gồm:</p>\r\n\r\n<p>- Tiền sử gia đ&igrave;nh: Nếu cha hoặc anh trai bị bệnh, bạn c&oacute; khả năng mắc gấp 2-3 lần b&igrave;nh thường. C&agrave;ng nhiều người th&acirc;n gặp phải bệnh n&agrave;y, nguy cơ của bạn c&agrave;ng cao.</p>\r\n\r\n<p>- Chủng tộc: Đ&agrave;n &ocirc;ng Mỹ gốc Phi c&oacute; nhiều khả năng mắc bệnh hơn. C&aacute;c nh&agrave; khoa học vẫn chưa l&yacute; giải được điều n&agrave;y.&nbsp;</p>\r\n\r\n<p><strong>Phải điều trị ngay lập tức khi ph&aacute;t hiện bệnh</strong></p>\r\n\r\n<p><em>Thực tế:&nbsp;</em>Bạn v&agrave; b&aacute;c sĩ sẽ c&ugrave;ng kiểm tra, b&agrave;n bạc để quyết định thời điểm điều trị. Liệu tr&igrave;nh c&oacute; thể phụ thuộc v&agrave;o giai đoạn bệnh, c&aacute;c bệnh l&yacute; kh&aacute;c. Nếu điều trị kh&ocirc;ng l&agrave;m tốt hơn t&igrave;nh trạng sức khỏe, b&aacute;c sĩ c&oacute; thể c&acirc;n nhắc &quot;gi&aacute;m s&aacute;t chủ động&quot; v&agrave; c&oacute; quyết định ph&ugrave; hợp khi cần thiết.</p>\r\n\r\n<p><strong>Mức PSA cao c&oacute; nghĩa l&agrave; bạn bị ung thư tuyến tiền liệt</strong></p>\r\n\r\n<p><em>Thực tế:</em>&nbsp;Kh&ocirc;ng nhất thiết. C&aacute;c bệnh kh&aacute;c ở tuyến tiền liệt c&oacute; thể l&agrave;m tăng mức PSA (kh&aacute;ng nguy&ecirc;n đặc hiệu của tuyến tiền liệt). Nồng độ PSA cao l&agrave; gợi &yacute; gi&uacute;p b&aacute;c sĩ quyết định c&acirc;n nhắc l&agrave;m th&ecirc;m c&aacute;c x&eacute;t nghiệm kh&aacute;c để kiểm tra ung thư tuyến tiền liệt. Nếu mức PSA đi xuống sau khi điều trị ung thư, đ&oacute; l&agrave; điều tuyệt vời.&nbsp;</p>\r\n\r\n<p><strong>Nếu bị ung thư tuyến tiền liệt, bạn chắc chắn chết v&igrave; n&oacute;</strong></p>\r\n\r\n<p><em>Thực tế:</em>&nbsp;Nếu kiểm so&aacute;t bệnh tốt, bạn c&oacute; khả năng sống đến gi&agrave; hoặc sẽ chết v&igrave; một số nguy&ecirc;n nh&acirc;n kh&aacute;c. Tuy nhi&ecirc;n điều n&agrave;y kh&ocirc;ng c&oacute; nghĩa việc kiểm tra ung thư tuyến tiền liệt kh&ocirc;ng quan trọng. Ph&aacute;t hiện sớm bệnh rất quan trọng để tăng hiệu quả điều trị.</p>\r\n', 2, 1, '2017-12-16 22:28:54', 'Suc Khoe', 'hinhanh/2017/12/Suc Khoe/suckhoe7.jpg'),
(43, 'Lotte Mart tài trợ phẫu thuật dị tật môi cho 40 trẻ', '<h2>Ca phẫu thuật sẽ được thực hiện v&agrave;o th&aacute;ng 3/2018 tại Bệnh viện Đại học Y dược TP HCM, tổng chi ph&iacute; 200 triệu đồng.</h2>\r\n\r\n<p>Lotte Mart Việt Nam vừa t&agrave;i trợ 200 triệu đồng cho Quỹ phẫu thuật nụ cười &quot;Operation Smile&quot;. Đ&acirc;y l&agrave; lần hợp t&aacute;c thứ 3 giữa hai b&ecirc;n, nhằm mang lại nụ cười cho trẻ dị tật m&ocirc;i v&agrave; hở h&agrave;m ếch, gi&uacute;p c&aacute;c em nhỏ dễ h&ograve;a nhập cuộc sống.</p>\r\n\r\n<p>Trước đ&oacute;, hai b&ecirc;n đ&atilde; triển khai phẫu thuật miễn ph&iacute; cho hơn 40 em nhỏ v&agrave;o th&aacute;ng 3/2016 v&agrave; 3/2017, tại Bệnh viện Đại học Y dược TP HCM.</p>\r\n\r\n<p>&Ocirc;ng Kang Min Ho - Tổng gi&aacute;m đốc Lotte Mart Việt Nam cho biết, số tiền t&agrave;i trợ 200 triệu được tr&iacute;ch từ quỹ &quot;Tấm l&ograve;ng Choice L&quot; (Choice L Foundation) của LOTTE Mart. Ph&aacute;t biểu tại lễ hợp t&aacute;c, &ocirc;ng kh&iacute;ch lệ Quỹ phẫu thuật nụ cười v&agrave; c&aacute;c tổ chức cộng đồng kh&aacute;c sẽ ng&agrave;y c&agrave;ng ph&aacute;t triển, gi&uacute;p đỡ th&ecirc;m nhiều người c&oacute; ho&agrave;n cảnh kh&oacute; khăn.</p>\r\n\r\n<p>Hơn 30 năm hoạt động tại Việt Nam, Quỹ phẫu thuật nụ cười đ&atilde; mang đến h&agrave;ng ngh&igrave;n cơ hội phẫu thuật miễn ph&iacute; cho trẻ em v&ugrave;ng kh&oacute; khăn. Kh&ocirc;ng &iacute;t trẻ lớn l&ecirc;n c&oacute; tương lai tươi s&aacute;ng hơn nhờ được mổ dị tật m&ocirc;i v&agrave; hở h&agrave;m ếch.</p>\r\n\r\n<p>Nhiều hoạt động cộng đồng cũng được Quỹ tấm l&ograve;ng Choice L thực hiện suốt thời gian qua. Quỹ đ&atilde; trao trao 48 học b&ocirc;̉ng cho sinh vi&ecirc;n xu&acirc;́t sắc; t&ocirc;̉ chức chuy&ecirc;́n đi mơ ước cho trẻ m&ocirc;̀ c&ocirc;i, khuy&ecirc;́t t&acirc;̣t; tặng quà người nghèo; cứu trợ người d&acirc;n v&ugrave;ng b&atilde;o Kh&aacute;nh H&ograve;a - Ph&uacute; Y&ecirc;n...</p>\r\n', 2, 1, '2017-12-16 22:32:01', 'Suc Khoe', 'hinhanh/2017/12/Suc Khoe/suckhoe2.jpg'),
(56, 'Ronaldo huyền thoại bóng đá thế giới', '<p>Ronaldo hiện cũng giữ c&aacute;c kỷ lục cầu thủ ghi nhiều b&agrave;n nhất cho Real (420 b&agrave;n), cho tuyển Bồ Đ&agrave;o Nha (79), giải Champions League (114), Euro (9) v&agrave; trong một m&ugrave;a Champions League (17).</p>\r\n\r\n<p>&quot;Nếu đổi t&ecirc;n, Ronaldo n&ecirc;n lấy t&ecirc;n l&agrave; &#39;chuy&ecirc;n gia ph&aacute; kỷ lục&#39;, để tương xứng với bảng th&agrave;nh t&iacute;ch khổng lồ của bản th&acirc;n&quot;, tờ&nbsp;<em>Marca</em>&nbsp;đưa ra lời khuy&ecirc;n.</p>\r\n\r\n<p>B&agrave;n thắng mới nhất cũng đ&aacute;nh dấu trận thứ ba li&ecirc;n tiếp Ronaldo lập c&ocirc;ng. Chuỗi trận u &aacute;m đầu m&ugrave;a đang l&ugrave;i v&agrave;o qu&aacute; khứ v&agrave; CR7 sắp lấy lại phong độ cao nhất. Nếu cứ chơi như vậy, Ronaldo c&oacute; thể ph&aacute; th&ecirc;m nhiều kỷ lục kh&aacute;c.</p>\r\n', 5, 1, '2017-12-16 23:13:45', 'The Thao', 'hinhanh/2017/12/The Thao/thethao10.jpg'),
(57, 'Thái Lan không thích gặp Việt Nam trận tranh giải ba Cup M-150', '<p>Tối qua 13/12, U23 Th&aacute;i Lan để vuột chiếc v&eacute; v&agrave;o chung kết Cup M-150 v&agrave;o tay U23 Nhật Bản sau trận thua 0-1 trước Triều Ti&ecirc;n. Mục ti&ecirc;u đề ra của đội chủ nh&agrave; đ&atilde; kh&ocirc;ng ho&agrave;n th&agrave;nh, v&agrave; họ phải gặp Việt Nam ở trận tranh hạng ba.</p>\r\n\r\n<p>Sau trận đấu, HLV Zoran Jankovic chia sẻ: &quot;Ch&uacute;ng t&ocirc;i đ&atilde; nắm lợi thế lớn trước trận đấu nhưng c&aacute;c cầu thủ đ&atilde; kh&ocirc;ng thực hiện được. Th&aacute;i Lan muốn gặp Uzbekistan ở trận chung kết hơn chứ kh&ocirc;ng muốn đọ sức với Việt Nam. Nhưng b&acirc;y giờ kết cục đ&atilde; an b&agrave;i, gặp đội n&agrave;o cũng tốt&quot;.</p>\r\n\r\n<p>Th&aacute;i Lan tổ chức giải giao hữu M-150 để gi&uacute;p thầy tr&ograve; &ocirc;ng Jankovic c&oacute; cơ hội cọ x&aacute;t với c&aacute;c đội b&oacute;ng h&agrave;ng đầu ch&acirc;u lục trước khi l&ecirc;n đường dự v&ograve;ng chung kết U23 ch&acirc;u &Aacute;. Nhưng mục ti&ecirc;u của họ đ&atilde; kh&ocirc;ng trọn vẹn khi Nhật Bản chỉ mang đội h&igrave;nh U20 c&ograve;n Triều Ti&ecirc;n cũng &ldquo;giấu b&agrave;i&rdquo;khi chưa mang đến giải đấu lực lượng h&ugrave;ng hậu nhất. Trong trận đấu cuối c&ugrave;ng, họ gặp lại Việt Nam, đội b&oacute;ng qu&aacute; quen thuộc ở c&aacute;c đấu trường cấp khu vực nhiều năm qua.</p>\r\n\r\n<p>&Ocirc;ng thầy người Serbia nhận x&eacute;t: &quot;Ch&uacute;ng t&ocirc;i muốn gặp c&aacute;c đội mạnh ở giải đấu n&agrave;y để t&iacute;ch luỹ kinh nghiệm. C&ograve;n Việt Nam kh&ocirc;ng hơn ch&uacute;ng t&ocirc;i. Tuy nhi&ecirc;n, ở trận tranh giải ba, ch&uacute;ng t&ocirc;i cũng cống hiến hết m&igrave;nh v&igrave; kh&aacute;n giả&quot;.</p>\r\n\r\n<p>Trận U23 Th&aacute;i Lan - U23 Việt Nam diễn ra l&uacute;c 16h00 ng&agrave;y 15/12.</p>\r\n', 5, 1, '2017-12-16 23:16:59', 'The Thao', 'hinhanh/2017/12/The Thao/thethao2.jpg'),
(58, 'Zidane phát cáu vì công nghệ kiểm tra bàn thắng bằng video', '<p>Casemiro l&agrave; người đưa b&oacute;ng v&agrave;o lưới Al-Jazira trong hiệp một, trận b&aacute;n kết FIFA Club World Cup tối 13/12. Trong khi tiền vệ người Brazil dứt điểm bằng một c&uacute; đ&aacute;nh đầu, đồng đội Karim Benzema rơi v&agrave;o thế việt vị v&agrave; chủ động kh&ocirc;ng tham gia v&agrave;o t&igrave;nh huống. Nhưng c&ocirc;ng nghệ kiểm tra b&agrave;n thắng bằng video VAR x&aacute;c định Benzema vẫn rơi v&agrave;o thế việt vị v&agrave; kh&ocirc;ng c&ocirc;ng nhận th&agrave;nh quả của Casemiro.</p>\r\n\r\n<p>Sau khi c&ocirc;ng nhận b&agrave;n thắng cho Real, trọng t&agrave;i nhận được t&iacute;n hiệu từ VAR v&agrave; đảo ngược quyết định.</p>\r\n\r\n<p>&quot;Ch&uacute;ng ta phải đợi ba hay bốn ph&uacute;t để x&aacute;c định một b&agrave;n thắng, v&agrave; đ&oacute; l&agrave; điều kh&ocirc;ng hề dễ chịu. T&ocirc;i đo&aacute;n b&agrave;n thắng bị hủy bỏ do lỗi việt vị. Nhận định đ&oacute; c&oacute; vẻ kỳ lạ&quot;,&nbsp;HLV Zinedine Zidane ph&aacute;t c&aacute;u sau trận đấu.</p>\r\n\r\n<p>Ngo&agrave;i pha hỏng ăn của Casemiro, Real c&ograve;n bỏ lỡ cả t&aacute; cơ hội trong hiệp một. B&oacute;ng thậm ch&iacute; bốn lần chạm cột dọc hay x&agrave; ngang của Al-Jazira.&nbsp;&quot;Nửa đầu trận đấu rất kỳ quặc. Ch&uacute;ng t&ocirc;i kh&ocirc;ng ghi được b&agrave;n n&agrave;o sau rất nhiều cơ hội. Nhưng ch&uacute;ng t&ocirc;i c&oacute; sự ki&ecirc;n nhẫn v&agrave; biết sẽ c&ograve;n nhiều cơ hội gi&agrave;nh chiến thắng&quot;, Zidane đ&aacute;nh gi&aacute;.</p>\r\n\r\n<p>Ở trận chung kết, Real sẽ gặp đại diện Nam Mỹ l&agrave; Gremio - đội đ&atilde; thắng Pachuca với tỷ số 1-0 ở&nbsp;trận b&aacute;n kết kh&aacute;c.&nbsp;&quot;T&ocirc;i đ&atilde; xem trận đấu của Gremio. Th&uacute; thực l&agrave; t&ocirc;i kh&ocirc;ng hiểu lắm về giải v&ocirc; địch Brazil. T&ocirc;i chỉ biết Gremio l&agrave; một đội b&oacute;ng mạnh v&agrave; ch&uacute;ng t&ocirc;i cần cải thiện phong độ so với h&ocirc;m nay&quot;, Zidane cho biết.</p>\r\n', 5, 1, '2017-12-16 23:18:46', 'The Thao', 'hinhanh/2017/12/The Thao/thethao8.jpg'),
(59, 'Djokovic mạnh hơn Fererder ở đỉnh cao', '<p>&nbsp;</p>\r\n\r\n<p>&quot;Đấu với Federer lu&ocirc;n kh&oacute; khăn, v&igrave; anh ấy lu&ocirc;n biết c&aacute;ch khiến t&ocirc;i đ&aacute;nh tệ đi. So với khi gặp Djokovic v&agrave; Nadal, cảm gi&aacute;c t&ocirc;i c&oacute; thể chơi tốt ngo&agrave;i mong đợi&quot;, Soderling n&oacute;i với&nbsp;<em>Tennismash</em>. &quot;Nhưng t&ocirc;i nghĩ Djokovic sẽ thắng Federer nếu cả hai ở phong độ cao nhất. Djokovic di chuyển qu&aacute; hợp l&yacute; v&agrave; phản c&ocirc;ng tuyệt vời. Federer sẽ gặp kh&oacute; bởi lối đ&aacute;nh tốc độ từ anh ta, dẫn đến rối loạn. D&ugrave; sao, đ&acirc;y cũng l&agrave; &yacute; kiến c&aacute; nh&acirc;n&rdquo;.</p>\r\n\r\n<p>Th&agrave;nh t&iacute;ch tốt nhất của Soderling l&agrave; ng&ocirc;i &aacute; qu&acirc;n Roland Garros c&aacute;c năm 2009 v&agrave; 2010, khi anh lần lượt thua Federer v&agrave; Nadal. Tay vợt người Thụy Điển từng 17 lần so vợt c&ugrave;ng Federer v&agrave; chỉ thắng một trận. Đ&oacute; cũng l&agrave; th&agrave;nh t&iacute;ch của anh trong bảy lần gặp Djokovic. Trong khi đ&oacute;, tay vợt người Serbia đang c&oacute; th&agrave;nh t&iacute;ch đối đầu nhỉnh hơn Federer với 23 chiến thắng qua 45 trận.</p>\r\n\r\n<p>Djokovic vẫn chưa thi đấu trở lại từ khi bị Tomas Berdych đ&aacute;nh bại ở tứ kết Wimbledon 2017, bởi chấn thương. Cựu số một thế giới sẽ t&aacute;i xuất ở Australia Mở rộng th&aacute;ng 1/2018, c&ugrave;ng nhiều t&ecirc;n tuổi như Andy Murray, Stan Wawrinka, Milos Raonic hay Kei Nishikori. Soderling k&ecirc;u gọi ATP thay đổi lịch thi đấu để hạn chế nguy cơ chấn thương cho c&aacute;c tay vợt.</p>\r\n', 5, 1, '2017-12-16 23:24:41', 'The Thao', 'hinhanh/2017/12/The Thao/thethao1.jpg'),
(60, 'Alexander Zverev: ‘Không ai hoàn hảo, trừ Federer’', '<p>Zverev từng tiết lộ anh l&agrave; fan ruột của Federer, khi quật ng&atilde; tay vợt người Thụy Sĩ ở chung kết Rogers Cup. &ldquo;Chiến thắng n&agrave;y c&oacute; &yacute; nghĩa đặc biệt với t&ocirc;i, bởi Federer l&agrave; người h&ugrave;ng trong mắt t&ocirc;i từ khi c&ograve;n nhỏ. Anh ấy l&agrave; tay vợt hay nhất lịch sử. Hạ gục Federer tại bất k&igrave; trận chung kết n&agrave;o cũng tuyệt hảo, chưa n&oacute;i đến giải Masters 1000&rdquo;, Zverev n&oacute;i hồi th&aacute;ng 8/2017.</p>\r\n\r\n<p>Hai tay vợt ch&ecirc;nh nhau 16 tuổi đ&atilde; c&oacute; năm lần so vợt, với lợi thế đối đầu 3-2 cho &ldquo;T&agrave;u tốc h&agrave;nh&rdquo;. T&agrave;i năng 20 tuổi kết th&uacute;c năm 2017 m&agrave; kh&ocirc;ng thể v&agrave;o tứ kết Grand Slam, d&ugrave; đ&atilde; v&ocirc; địch Rome Masters v&agrave; Rogers Cup. Zverev chưa từng thắng một tay vợt trong top 50 ở bốn giải lớn. Do đ&oacute;, mục ti&ecirc;u lớn nhất của tay vợt đứng thứ tư ATP&nbsp;trong năm tới l&agrave; Grand Slam.</p>\r\n', 5, 1, '2017-12-16 23:26:23', 'The Thao', 'hinhanh/2017/12/The Thao/thethao7.jpg'),
(61, 'Bóng đá Việt Nam ngày nay', '<p><em>&nbsp;V&igrave; sao đội b&oacute;ng của anh quyết định chia tay một loạt cầu thủ, trong đ&oacute; c&oacute; nhiều người từng l&agrave; trụ cột hoặc c&oacute; tiếng tăm - thậm ch&iacute; l&agrave; cựu tuyển thủ quốc gia?</em></p>\r\n\r\n<p>- CLB TP HCM trụ hạng m&ugrave;a vừa qua, theo t&ocirc;i, l&agrave; một th&agrave;nh c&ocirc;ng lớn. Ai nh&igrave;n v&agrave;o cũng thấy lực lượng của ch&uacute;ng t&ocirc;i kh&ocirc;ng mạnh, nhưng c&aacute;c cầu thủ đ&atilde; cố gắng hết sức để về đ&iacute;ch an to&agrave;n. Ch&uacute;ng t&ocirc;i quyết định chia tay 13 cầu thủ, một phần v&igrave; họ hết hợp đồng v&agrave; một phần v&igrave; t&ocirc;i cũng như ban huấn luyện đ&atilde; c&oacute; những đ&aacute;nh gi&aacute; về chuy&ecirc;n m&ocirc;n của họ. Đa số cầu thủ đ&oacute;&nbsp;đ&atilde; ngo&agrave;i 30 tuổi, như Duy Nam, Esele, Phước Vĩnh, Victor&hellip; Ch&uacute;ng t&ocirc;i muốn d&agrave;nh đất cho c&aacute;c cầu thủ trẻ. Sang m&ugrave;a giải mới, CLB TP HCM sẽ đ&ocirc;n một số cầu thủ trẻ từ U21 TP HCM l&ecirc;n thi đấu. Đội h&igrave;nh của CLB sẽ c&oacute; khoảng 10 cầu thủ l&agrave; người TP HCM.</p>\r\n\r\n<p><em>- Anh đang n&oacute;i về trẻ ho&aacute; lực lượng, vậy tại sao Anh T&agrave;i - đang thi đấu cho đội U23 Việt Nam ở giải đấu tại Th&aacute;i Lan - cũng nằm trong danh s&aacute;ch&nbsp;ra đi?</em></p>\r\n\r\n<p>- Sau giai đoạn một V-League 2017, ch&uacute;ng t&ocirc;i đ&atilde; đ&agrave;m ph&aacute;n với mục đ&iacute;ch giữ Anh T&agrave;i ở lại. Tuy nhi&ecirc;n, Anh T&agrave;i n&oacute;i rằng cậu ấy muốn đến đội b&oacute;ng kh&aacute;c để t&igrave;m một m&ocirc;i trường tốt, để được thi đấu thường xuy&ecirc;n n&ecirc;n t&ocirc;i t&ocirc;n trọng quyết định của cậu ấy.</p>\r\n\r\n<p><em>- Giới chuy&ecirc;n m&ocirc;n cũng như người h&acirc;m mộ kh&ocirc;ng khỏi bất ngờ trước&nbsp;việc CLB TP HCM đồng loạt chia tay gần nửa đội h&igrave;nh chỉ sau một m&ugrave;a giải. Anh nghĩ sao về điều n&agrave;y?</em></p>\r\n\r\n<p>- Chỉ ở Việt Nam, những chuyện như vậy mới to t&aacute;t, chứ ở m&ocirc;i trường b&oacute;ng đ&aacute; thế giới, điều n&agrave;y hết sức b&igrave;nh thường. B&oacute;ng đ&aacute; c&oacute; quy luật của sự đ&agrave;o thải. Anh kh&ocirc;ng c&oacute; chuy&ecirc;n m&ocirc;n, kh&ocirc;ng thi đấu tốt, kh&ocirc;ng ph&ugrave; hợp với ti&ecirc;u ch&iacute; của đội b&oacute;ng th&igrave; phải ra đi. Hồi t&ocirc;i sang Nhật Bản thi đấu cho Consadole Sapporo, t&ocirc;i đ&atilde; nhận ra điều n&agrave;y. Chỉ sau một m&ugrave;a giải, nếu nhận thấy cầu thủ chưa ph&ugrave; hợp, c&ograve;n bản th&acirc;n CLB c&oacute; mục ti&ecirc;u cao hơn ở m&ugrave;a giải mới, c&aacute;c CLB Nhật Bản lập tức thanh l&yacute; cả loạt cầu thủ. Kh&ocirc;ng vấn đề g&igrave; xảy ra cả. C&ograve;n với CLB TP HCM, kh&ocirc;ng chỉ sau m&ugrave;a b&oacute;ng n&agrave;y, m&agrave; cả m&ugrave;a tới, nếu cầu thủ kh&ocirc;ng đ&aacute;p ứng được chuy&ecirc;n m&ocirc;n, t&ocirc;i cũng sẽ c&oacute; những quyết định tương tự.</p>\r\n', 5, 1, '2017-12-16 23:28:19', 'The Thao', 'hinhanh/2017/12/The Thao/thethao5.jpg'),
(62, 'Đội bóng Công Vinh chia tay hàng loạt cầu thủ', '<h2>Trưa 14/12, CLB TP HCM ra th&ocirc;ng b&aacute;o chấm dứt hợp đồng với 13 cầu thủ từng thi đấu trong m&ugrave;a giải V-League 2017.</h2>\r\n\r\n<p>Danh s&aacute;ch gồm: Đỗ Thanh Sang, Lương Văn Nh&agrave;n, Nguyễn Tuấn Anh, V&otilde; Duy Nam, Nguyễn Anh T&agrave;i, Trịnh Quang Vinh, Ch&acirc;u L&ecirc; Phước Vĩnh, L&ecirc; Mạnh Dũng, Nguyễn Trọng Phi, Nguyễn Hồng Việt, Quốc Thiện Esele, Victor và Da Sylva.</p>\r\n\r\n<p>Trong danh s&aacute;ch n&agrave;y c&oacute; nhiều cầu thủ thường xuy&ecirc;n ra s&acirc;n đ&aacute; ch&iacute;nh ở V-League như thủ m&ocirc;n Quốc Thiện Esele, Victor, Phước Vĩnh, Thanh Sang&hellip; Tuyển thủ U23 Nguyễn Anh T&agrave;i đang thi đấu ở Cup M-150 Th&aacute;i Lan cũng nhận được tin bị thanh l&yacute;.</p>\r\n\r\n<p>M&ugrave;a giải 2017, CLB TP HCM đứng thứ 12 (trong số 14 đội) v&agrave; gi&agrave;nh quyền trụ hạng sớm. Tuy nhi&ecirc;n, chia sẻ c&ugrave;ng<em>&nbsp;VnExpress</em>, quyền Chủ tịch C&ocirc;ng Vinh tiết lộ: &ldquo;M&ugrave;a mới, CLB TP HCM sẽ thay m&aacute;u lực lượng khoảng 80% để đội c&oacute; th&agrave;nh t&iacute;ch tốt hơn. L&agrave;m b&oacute;ng đ&aacute; phải c&oacute; sự cạnh tranh v&agrave; đoạt th&agrave;nh t&iacute;ch chứ kh&ocirc;ng chỉ chăm chăm trụ hạng&rdquo;.</p>\r\n\r\n<p>Theo cựu tiền đạo tuyển Việt Nam, CLB TP HCM sẽ c&oacute; những bản hợp đồng đắt gi&aacute; trong m&ugrave;a giải mới. Trong đ&oacute;, nổi bật l&agrave; Trần Phi Sơn - người đ&atilde; hết hợp đồng với SLNA. Ngo&agrave;i ra, C&ocirc;ng Vinh đang x&uacute;c tiến với nhiều ngoại binh chất lượng, với những mức lương cao v&agrave; sẽ sớm ra mắt trong thời gian tới.</p>\r\n\r\n<p>Ngo&agrave;i việc l&agrave;m mới lực lượng, tr&ecirc;n băng ghế chỉ đạo, CLB TP HCM cũng sắp l&agrave;m lễ ra mắt HLV trưởng Toshiya Miura.</p>\r\n', 5, 1, '2017-12-16 23:30:33', 'The Thao', 'hinhanh/2017/12/The Thao/thethao11.jpg'),
(63, 'Đám cưới Hoàng Tử Harry diễn ra vào ngày 19/5 năm sau', '<p>Ho&agrave;ng tử Anh Harry v&agrave; h&ocirc;n th&ecirc;&nbsp;Meghan Markle sẽ kết h&ocirc;n v&agrave;o ng&agrave;y thứ bảy, 19/5 năm sau,&nbsp;<em>BBC</em>&nbsp;h&ocirc;m qua dẫn tin từ Cung điện&nbsp;Kensington cho hay.</p>\r\n\r\n<p>Thời điểm tổ chức đ&aacute;m cưới n&agrave;y kh&aacute;c với truyền thống của Ho&agrave;ng gia Anh, khi c&aacute;c lễ cưới thường diễn ra v&agrave;o ng&agrave;y trong tuần. Nữ ho&agrave;ng Anh kết h&ocirc;n v&agrave;o thứ 5 c&ograve;n Ho&agrave;ng tử William c&oacute; đ&aacute;m cưới v&agrave;o thứ 6.</p>\r\n\r\n<p>H&ocirc;n lễ được tổ chức tại nh&agrave; thờ Th&aacute;nh George ở l&acirc;u đ&agrave;i Windsor.&nbsp;To&agrave;n bộ chi ph&iacute; đ&aacute;m cưới do Ho&agrave;ng gia Anh chi trả.</p>\r\n\r\n<p>Ho&agrave;ng tử Harry, 33 tuổi, v&agrave; h&ocirc;n th&ecirc; người Mỹ Markle, 36 tuổi, đ&atilde; đ&iacute;nh h&ocirc;n hồi th&aacute;ng trước.&nbsp;Markle&nbsp;sẽ được rửa tội v&agrave; gia nhập Gi&aacute;o hội Anh trước khi trở th&agrave;nh c&ocirc; d&acirc;u.&nbsp;Đ&aacute;m cưới dự kiến diễn ra một th&aacute;ng sau khi C&ocirc;ng nương Kate sinh con thứ ba.</p>\r\n\r\n<p>Cung điện&nbsp;Kensington đầu tuần th&ocirc;ng b&aacute;o Ho&agrave;ng tử Harry v&agrave;&nbsp;Meghan Markle sẽ c&ugrave;ng đ&oacute;n Gi&aacute;ng sinh với Ho&agrave;ng gia.&nbsp;Việc Nữ ho&agrave;ng Elizabeth mời h&ocirc;n th&ecirc; Ho&agrave;ng tử Harry tham gia sự kiện truyền thống được b&agrave; tổ chức từ năm 1988 tiếp tục thể hiện sự ch&agrave;o đ&oacute;n của Ho&agrave;ng gia Anh d&agrave;nh cho Markle.</p>\r\n\r\n<p>Harry v&agrave;&nbsp;Markle&nbsp;được cho l&agrave; gặp nhau v&agrave;o th&aacute;ng 5/2016. Cung điện Kensington ch&iacute;nh thức x&aacute;c nhận mối quan hệ giữa hai người, sau khi nhiều đồn đo&aacute;n xuất hiện tr&ecirc;n truyền th&ocirc;ng.</p>\r\n', 7, 1, '2017-12-16 23:33:45', 'The Gioi', 'hinhanh/2017/12/The Gioi/thegioi8.jpg'),
(64, 'Hải quân Mỹ sẵn sàng chiến đấu kém nhất trong vòng 30 năm', '<p>&quot;Lực lượng t&agrave;u chiến c&oacute; quy m&ocirc; nhỏ c&ugrave;ng số lượng &iacute;t thủy thủ đang phải chật vật để duy tr&igrave; y&ecirc;u cầu sẵn s&agrave;ng chiến đấu của hải qu&acirc;n Mỹ&quot;,&nbsp;<em>Sputnik&nbsp;</em>dẫn b&aacute;o c&aacute;o do hải qu&acirc;n Mỹ c&ocirc;ng bố h&ocirc;m 14/12. Hải qu&acirc;n Mỹ khẳng định lực lượng n&agrave;y đang ở t&igrave;nh trạng sẵn s&agrave;ng chiến đấu k&eacute;m nhất trong v&ograve;ng 30 năm qua.</p>\r\n\r\n<p>B&aacute;o c&aacute;o mang t&ecirc;n &quot;Đ&aacute;nh gi&aacute; khả năng sẵn s&agrave;ng chiến lược&quot; được thực hiện từ đầu th&aacute;ng 9, sau loạt vụ tai nạn l&agrave;m hai t&agrave;u khu trục Mỹ bị hư hỏng nặng v&agrave; khiến 20 thủy thủ thiệt mạng. B&aacute;o c&aacute;o được thực hiện nhằm l&agrave;m r&otilde; nguy&ecirc;n nh&acirc;n dẫn tới c&aacute;c sự cố n&agrave;y, đồng thời t&igrave;m giải ph&aacute;p khắc phục v&agrave; tăng sức chiến đấu cho hải qu&acirc;n Mỹ.</p>\r\n\r\n<p>Một l&yacute; do lớn dẫn tới loạt tai nạn l&agrave; số lượng t&agrave;u sẵn s&agrave;ng hoạt động trong bi&ecirc;n chế hải qu&acirc;n Mỹ hiện nay. Lực lượng n&agrave;y phải duy tr&igrave; &iacute;t nhất 100 chiến hạm ngo&agrave;i khơi v&agrave;o mọi thời điểm trong 30 năm qua. Tuy nhi&ecirc;n, số lượng t&agrave;u chiến của Mỹ đ&atilde; bị cắt giảm từ 529 chiếc hồi năm 1991 xuống chỉ c&ograve;n 279 t&agrave;u trong năm nay.</p>\r\n\r\n<p>&quot;C&aacute;ch duy nhất để hải qu&acirc;n Mỹ duy tr&igrave; y&ecirc;u cầu t&aacute;c chiến l&agrave; giảm thời gian huấn luyện v&agrave; bảo dưỡng&quot;, Bộ trưởng Hải qu&acirc;n Mỹ Richard V. Spencer cho biết. Điều n&agrave;y khiến thủy thủ kh&ocirc;ng được đ&agrave;o tạo một c&aacute;ch b&agrave;i bản, trong khi c&aacute;c chiến hạm c&oacute; thể chịu nhiều vấn đề kỹ thuật trong qu&aacute; tr&igrave;nh vận h&agrave;nh.</p>\r\n\r\n<p>&Ocirc;ng Spencer cho biết việc thay đổi t&igrave;nh trạng n&agrave;y sẽ kh&ocirc;ng thể diễn ra trong ngắn hạn. N&oacute; đ&ograve;i hỏi thời gian d&agrave;i, c&ugrave;ng sự tập trung với nguồn ng&acirc;n s&aacute;ch kh&ocirc;ng bị hạn chế trong những năm tới.</p>\r\n', 7, 1, '2017-12-16 23:37:12', 'The Gioi', 'hinhanh/2017/12/The Gioi/thegioi9.jpg'),
(65, 'Lưới lửa phòng không khiến không quân Israel ôm hận năm 1973', '<p>Kh&ocirc;ng qu&acirc;n Israel được coi l&agrave; lực lượng thống trị bầu trời Trung Đ&ocirc;ng trong thập ni&ecirc;n 1960. Trong Chiến tranh s&aacute;u ng&agrave;y v&agrave;o năm 1967, Israel mất 46 m&aacute;y bay v&agrave; 24 phi c&ocirc;ng, nhưng đ&atilde; bắn hạ gần 400 m&aacute;y bay đối phương v&agrave; kiểm so&aacute;t bầu trời chỉ v&agrave;i giờ sau khi xung đột nổ ra.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, trong cuộc chiến Yom Kippur v&agrave;o năm 1973, li&ecirc;n qu&acirc;n Ai Cập - Syria đ&atilde; &aacute;p dụng chiến thuật ph&ograve;ng kh&ocirc;ng hiệu quả, khiến kh&ocirc;ng qu&acirc;n Israel trải qua những thời khắc đen tối v&agrave; kinh ho&agrave;ng nhất trong lịch sử, theo&nbsp;<em>WATM</em>.</p>\r\n\r\n<p>Ng&agrave;y 6/10/1973, với hy vọng gi&agrave;nh lại v&ugrave;ng l&atilde;nh thổ đ&atilde; mất v&agrave;o tay Israel trong cuộc chiến trước đ&oacute; 6 năm, Ai Cập v&agrave; Syria mở chiến dịch phối hợp tấn c&ocirc;ng Israel. Bị đ&aacute;nh &uacute;p bất ngờ, qu&acirc;n đội Israel đ&atilde; để lực lượng Ai Cập tiến s&acirc;u v&agrave;o b&aacute;n đảo Sinai, đồng thời bị Syria đ&aacute;nh bật khỏi cao nguy&ecirc;n Golan, hai khu vực m&agrave; họ chiếm được trước đ&oacute;.</p>\r\n\r\n<p>Chuy&ecirc;n gia qu&acirc;n sự Harold Hutchison cho rằng li&ecirc;n qu&acirc;n Ai Cập - Syria hiểu r&otilde; ưu thế qu&acirc;n sự của Israel, đặc biệt l&agrave; lực lượng kh&ocirc;ng qu&acirc;n được trang bị hiện đại v&agrave; d&agrave;y dặn kinh nghiệm. Do vậy, trong cuộc chiến Yom Kippur, hai quốc gia n&agrave;y đ&atilde; &aacute;p dụng chiến thuật ph&ograve;ng kh&ocirc;ng lợi hại, kết hợp giữa t&ecirc;n lửa ph&ograve;ng kh&ocirc;ng 2K12 &quot;Kub&quot; v&agrave; tổ hợp ph&aacute;o ph&ograve;ng kh&ocirc;ng tự h&agrave;nh ZSU-23-4 &quot;Shilka&quot;, nhằm bảo vệ lực lượng bộ binh trước đ&ograve;n kh&ocirc;ng k&iacute;ch của chiến đấu cơ Israel.</p>\r\n\r\n<p>Hệ thống ph&ograve;ng kh&ocirc;ng tầm trung 2K12 Kub được Li&ecirc;n X&ocirc; thiết kế năm 1959, trước khi sản xuất đại tr&agrave; trong giai đoạn 1968-1985. Một khẩu đội 2K12 gồm 4 xe chiến đấu, mỗi chiếc trang bị ba t&ecirc;n lửa, c&ugrave;ng 4 xe tải mang theo 12 quả đạn dự trữ v&agrave; hệ thống lắp đạn. To&agrave;n bộ c&aacute;c xe chiến đấu v&agrave; đ&agrave;i radar đều được đặt tr&ecirc;n khung gầm b&aacute;nh x&iacute;ch, gi&uacute;p tăng khả năng cơ động tr&ecirc;n địa h&igrave;nh phức tạp, dễ d&agrave;ng bảo vệ đội h&igrave;nh tăng thiết gi&aacute;p v&agrave; tr&aacute;nh bị đối phương đ&aacute;nh trả.</p>\r\n\r\n<p>Radar 1S91 của tổ hợp 2K12 gồm radar cảnh giới 1S11 v&agrave; đ&agrave;i dẫn bắn 1S31, c&ugrave;ng bộ thiết bị nhận diện địch ta (IFF) v&agrave; k&ecirc;nh b&aacute;m bắt quang học. Đ&agrave;i 1S91 được cho l&agrave; c&oacute; khả năng ph&aacute;t hiện mục ti&ecirc;u từ khoảng c&aacute;ch 75 km v&agrave; bắt đầu dẫn bắn trong tầm 28 km.</p>\r\n\r\n<p>T&ecirc;n lửa 9M336 được trang bị ng&ograve;i nổ chạm v&agrave; cận đ&iacute;ch, c&oacute; tầm bắn hiệu quả 24 km v&agrave; độ cao tối đa 14 km. Quả đạn sử dụng hệ thống đẩy với tầng sơ tốc chạy bằng nhi&ecirc;n liệu rắn, sau khi nhi&ecirc;n liệu ch&aacute;y hết, khoang chứa rỗng sẽ trở th&agrave;nh buồng đốt cho động cơ phản lực d&ograve;ng thẳng (ramjet). Thiết kế n&agrave;y được coi l&agrave; đi trước thời đại, vượt trội so với c&aacute;c t&ecirc;n lửa ph&ograve;ng kh&ocirc;ng c&ugrave;ng thời của phương T&acirc;y. 9M336 c&oacute; thể đạt tốc độ tối đa 3.460 km/h, đủ khả năng đe dọa mọi chiến đấu cơ hiện đại.</p>\r\n\r\n<p>Trong khi đ&oacute;, ZSU-23-4 &quot;Shilka&quot; được coi l&agrave; một trong những tổ hợp ph&ograve;ng kh&ocirc;ng tầm thấp nguy hiểm nhất thế giới. ZSU l&agrave; viết tắt của cụm từ &quot;hệ thống ph&aacute;o ph&ograve;ng kh&ocirc;ng tự h&agrave;nh&quot;, 23 l&agrave; cỡ n&ograve;ng t&iacute;nh bằng mm, trong khi 4 l&agrave; số n&ograve;ng ph&aacute;o tr&ecirc;n mỗi tổ hợp.</p>\r\n', 7, 1, '2017-12-16 23:38:13', 'The Gioi', 'hinhanh/2017/12/The Gioi/thegioi3.jpg'),
(66, 'Iraq xử tử 38 phiến quân IS và al-Qaeda', '<h2>Nh&agrave; chức tr&aacute;ch Iraq thi h&agrave;nh &aacute;n tử với c&aacute;c phiến qu&acirc;n bị buộc tội khủng bố, những người kh&ocirc;ng c&ograve;n quyền kh&aacute;ng c&aacute;o.</h2>\r\n\r\n<p>Iraq đ&atilde; xử tử 38 phiến qu&acirc;n IS v&agrave; al-Qaeda v&agrave;o ng&agrave;y 14/12,&nbsp;bằng h&igrave;nh thức treo cổ,&nbsp;<em>Fox News</em>&nbsp;h&ocirc;m qua đưa tin.</p>\r\n\r\n<p>Theo Bộ Tư ph&aacute;p Iraq, tất cả c&aacute;c phiến qu&acirc;n l&agrave; th&agrave;nh vi&ecirc;n của Nh&agrave; nước Hồi gi&aacute;o (IS) tự xưng, kh&ocirc;ng c&oacute; cơ hội kh&aacute;ng c&aacute;o v&igrave;&nbsp;bị buộc tội khủng bố. Tuy nhi&ecirc;n, một số quan chức ch&iacute;nh phủ cho hay v&agrave;i phiến qu&acirc;n thuộc tổ chức khủng bố al-Qaeda, trong đ&oacute; c&oacute; một người mang quốc tịch Thuỵ Điển.</p>\r\n\r\n<p>Việc xử tử diễn ra tại một nh&agrave; t&ugrave; ở&nbsp;th&agrave;nh phố&nbsp;Nassiriya, ph&iacute;a nam Iraq. Hồi cuối th&aacute;ng 9, tại đ&acirc;y, ch&iacute;nh quyền&nbsp;Iraq cũng đ&atilde; xử tử 43 phiến qu&acirc;n do phạm tội đ&aacute;nh bom v&agrave; s&aacute;t hại c&aacute;c th&agrave;nh vi&ecirc;n thuộc lực lượng an ninh nước n&agrave;y.</p>\r\n\r\n<p>Bộ trưởng Tư ph&aacute;p Iraq&nbsp;Haidar al-Zameli đ&atilde; tới chứng kiến qu&aacute; tr&igrave;nh xử tử, một quan chức cấp cao địa phương cho hay.</p>\r\n\r\n<p>Iraq tuần trước tuy&ecirc;n bố chiến thắng IS sau hơn ba năm giao tranh.&nbsp;Trước đ&oacute;, Syria cũng tuy&ecirc;n bố đẩy l&ugrave;i được IS khỏi l&atilde;nh thổ, đ&aacute;nh dấu sự sụp đổ của nh&oacute;m phiến qu&acirc;n kh&eacute;t tiếng nhất thế giới hiện nay.</p>\r\n', 7, 1, '2017-12-16 23:39:17', 'The Gioi', 'hinhanh/2017/12/The Gioi/thegioi1.jpg'),
(67, 'Ca khúc khải hoàn ở Syria, Putin khẳng định quyền lực với Trung Đông', '<h2>Chiến thắng ở Syria gi&uacute;p Nga củng cố vị thế vững chắc ở Trung Đ&ocirc;ng, khiến c&aacute;c cường quốc khu vực phải chơi theo luật của Putin.</h2>\r\n\r\n<p>Khi Tổng thống Nga Vladimir Putin hạ c&aacute;nh xuống căn cứ kh&ocirc;ng qu&acirc;n Hmeymim ở Syria h&ocirc;m 11/12, người đầu ti&ecirc;n ch&agrave;o đ&oacute;n &ocirc;ng ở ch&acirc;n cầu thang m&aacute;y bay l&agrave; một sĩ quan cấp cao qu&acirc;n đội Nga. Tổng thống Syria Bashar al-Assad sau đ&oacute; mới bước tới ch&agrave;o v&agrave; c&ugrave;ng &ocirc;ng Putin bước đi dọc đường băng, qua những chiếc ti&ecirc;m k&iacute;ch v&agrave; trực thăng tấn c&ocirc;ng Nga.</p>\r\n\r\n<p>Theo b&igrave;nh luận vi&ecirc;n Anshel Pfeffer của&nbsp;<em>Haaretz</em>, th&ocirc;ng điệp ph&aacute;t đi từ h&agrave;nh động n&agrave;y l&agrave; rất r&otilde; r&agrave;ng: Chuyến c&ocirc;ng du đầu ti&ecirc;n của &ocirc;ng Putin tới Syria kh&ocirc;ng phải l&agrave; một chuyến thăm cấp nh&agrave; nước th&ocirc;ng thường. Tổng thống Nga tới đ&acirc;y để kiểm tra tiền đồn mới nhất của Moscow ở nước ngo&agrave;i.</p>\r\n\r\n<p>Trong chuyến thăm bất ngờ n&agrave;y, Putin ra lệnh cho qu&acirc;n đội Nga đồn tr&uacute; tại Syria r&uacute;t một phần lực lượng về nước. &quot;C&aacute;c bạn đ&atilde; cho thấy những phẩm chất tốt đẹp nhất của người l&iacute;nh Nga - can trường, dũng cảm, tinh thần tập thể, sự quyết đo&aacute;n v&agrave; kỹ năng xuất sắc. Tổ quốc tự h&agrave;o về c&aacute;c bạn&quot;, &ocirc;ng Putin n&oacute;i trước c&aacute;c binh sĩ Nga tại căn cứ Hmeymim. &quot;C&aacute;c bạn sẽ về nh&agrave;, mang theo chiến thắng&quot;.</p>\r\n\r\n<p>Pfeffer cho rằng chuyến c&ocirc;ng du &quot;ca kh&uacute;c khải ho&agrave;n&quot; v&agrave; tuy&ecirc;n bố r&uacute;t qu&acirc;n trong chiến thắng&nbsp;n&agrave;y của &ocirc;ng Putin c&oacute; hai mục đ&iacute;ch rất r&otilde; r&agrave;ng: vừa trấn an dư luận trong nước, vừa thể hiện quyền lực v&agrave; sức ảnh hưởng vượt trội ở Trung Đ&ocirc;ng.</p>\r\n\r\n<p>C&aacute;c binh sĩ Nga đ&atilde; hiện diện ở Syria để tham gia chiến dịch ti&ecirc;u diệt phiến qu&acirc;n Nh&agrave; nước Hồi gi&aacute;o (IS) tự xưng được 27 th&aacute;ng. Lực lượng n&agrave;y đ&atilde; li&ecirc;n tiếp lập chiến c&ocirc;ng, yểm trợ hiệu quả cho qu&acirc;n đội ch&iacute;nh phủ Syria gi&agrave;nh lại những th&agrave;nh phố chiến lược, kiểm so&aacute;t được 98% l&atilde;nh thổ từ tay IS v&agrave; c&aacute;c nh&oacute;m nổi dậy. Tuy nhi&ecirc;n, với người d&acirc;n Nga, họ dường như chưa quen với việc để con em của họ tham chiến l&acirc;u d&agrave;i ở Trung Đ&ocirc;ng.</p>\r\n\r\n<p>B&igrave;nh luận vi&ecirc;n Hamza Hendawi v&agrave; Vladimir Isachenkov của&nbsp;<em>AP&nbsp;</em>cho rằng Putin dường như rất hy vọng rằng chuyến c&ocirc;ng du Trung Đ&ocirc;ng của &ocirc;ng sẽ khơi dậy đ&aacute;ng kể l&ograve;ng tự h&agrave;o quốc gia của người d&acirc;n Nga, đặc biệt l&agrave; trong bối cảnh cử tri nước n&agrave;y sắp bầu cử Tổng thống v&agrave;o ng&agrave;y 18/3. &Ocirc;ng Putin trước đ&oacute; đ&atilde; tuy&ecirc;n bố sẽ tiếp tục ra tranh cử Tổng thống.</p>\r\n\r\n<p>Giới quan s&aacute;t cho rằng với tỷ lệ ủng hộ khoảng 80% như hiện nay, &ocirc;ng Putin gần như chắc chắn sẽ t&aacute;i đắc cử v&agrave; những &acirc;m hưởng chiến thắng từ Syria c&oacute; thể gi&uacute;p &ocirc;ng củng cố điều n&agrave;y. Th&agrave;nh c&ocirc;ng trong chiến dịch qu&acirc;n sự ở Syria v&agrave; ảnh hưởng ng&agrave;y c&agrave;ng tăng của Nga ở Trung Đ&ocirc;ng sẽ g&oacute;p phần t&ocirc; đậm h&igrave;nh ảnh Putin như một người kh&ocirc;i phục vinh quang v&agrave; quyền lực to&agrave;n cầu m&agrave; nước Nga từng để mất sau khi Li&ecirc;n X&ocirc; tan r&atilde;.</p>\r\n\r\n<p>Tuy vậy, c&aacute;c chuy&ecirc;n gia cho rằng Putin sẽ kh&ocirc;ng r&uacute;t to&agrave;n bộ lực lượng ở Syria về nước v&agrave; c&aacute;c chiến đấu cơ của Moscow vẫn sẽ tiếp tục dội bom v&agrave;o c&aacute;c mục ti&ecirc;u cuối c&ugrave;ng m&agrave; IS đang cố thủ. Với chiến dịch qu&acirc;n sự n&agrave;y, Nga giờ đ&acirc;y đ&atilde; c&oacute; những căn cứ qu&acirc;n sự l&acirc;u d&agrave;i ở Trung Đ&ocirc;ng để l&agrave;m b&agrave;n đạp củng cố v&agrave; ph&aacute;t huy ảnh hưởng tại khu vực chiến lược n&agrave;y.</p>\r\n\r\n<p>Lần dừng ch&acirc;n ở căn cứ Hmeymim chỉ l&agrave; một phần trong chuyến c&ocirc;ng du Trung Đ&ocirc;ng của &ocirc;ng Putin, c&aacute;c chặng tiếp theo gồm thủ đ&ocirc; Cairo của Ai Cập v&agrave; Ankara của Thổ Nhĩ Kỳ. Khi Nga ph&aacute;t động chiến dịch can thiệp qu&acirc;n sự v&agrave;o Syria th&aacute;ng 9/2015, nhiều nh&agrave; quan s&aacute;t phương T&acirc;y dự đo&aacute;n rằng h&agrave;nh động n&agrave;y sẽ kết th&uacute;c trong thảm họa. Nhưng giờ đ&acirc;y, kh&ocirc;ng c&oacute; bất cứ ai nghi ngờ về chiến thắng của Moscow.</p>\r\n\r\n<p>Chiến dịch qu&acirc;n sự của Nga kh&ocirc;ng giống với những lần can thiệp &quot;v&igrave; d&acirc;n chủ v&agrave; tự do&quot; của Mỹ v&agrave; đồng minh trong những năm gần đ&acirc;y. Trong gần hai năm rưỡi qua, Nga kh&ocirc;ng c&oacute; bất cứ dấu hiệu nh&acirc;n nhượng n&agrave;o trước lực lượng khủng bố, sẵn s&agrave;ng dội bom, ph&oacute;ng t&ecirc;n lửa với cường độ m&atilde;nh liệt nhất để bẻ g&atilde;y &yacute; ch&iacute; kh&aacute;ng cự của phiến qu&acirc;n ở Aleppo, Palmyra hay bất cứ th&agrave;nh tr&igrave; n&agrave;o kh&aacute;c.</p>\r\n\r\n<p>Putin chơi theo quy tắc của Moscow v&agrave; những quy tắc đ&oacute; giờ đ&acirc;y đang mang lại th&agrave;nh c&ocirc;ng vang dội. &Ocirc;ng đ&atilde; bảo vệ được đồng minh al-Assad, củng cố li&ecirc;n minh khu vực chiến lược với sự tham gia của Iran, đảm bảo quyền tiếp cận l&acirc;u d&agrave;i v&ugrave;ng biển, v&ugrave;ng trời Địa Trung Hải cho qu&acirc;n đội Nga v&agrave; khẳng định vị thế bản th&acirc;n như l&atilde;nh đạo thế giới duy nhất c&oacute; ảnh hưởng thực sự ở Trung Đ&ocirc;ng.</p>\r\n\r\n<p>Khẳng định vị thế ở Trung Đ&ocirc;ng</p>\r\n\r\n<p>Putin quyết t&acirc;m thực hiện chiến dịch tốn k&eacute;m n&agrave;y bất chấp kinh tế Nga đang gặp rất nhiều kh&oacute; khăn do gi&aacute; dầu sụt giảm v&agrave; c&aacute;c lệnh cấm vận của phương T&acirc;y, bất chấp những lời đe dọa trả th&ugrave; của phiến qu&acirc;n Hồi gi&aacute;o tr&ecirc;n đất Nga. &Ocirc;ng l&agrave;m điều đ&oacute; v&igrave; &ocirc;ng x&aacute;c định đ&acirc;y l&agrave; cơ hội lịch sử để khẳng định quyền lực của Nga ở Trung Đ&ocirc;ng, khi Mỹ r&uacute;t lui khỏi khu vực.</p>\r\n\r\n<p>Obama đ&atilde; h&eacute; c&aacute;nh cửa Trung Đ&ocirc;ng cho Nga qua cuộc khủng hoảng vũ kh&iacute; h&oacute;a học ở Syria, c&ograve;n đương kim Tổng thống Mỹ Donald Trump đang mở toang c&aacute;nh cửa đ&oacute; bằng quyết định g&acirc;y tranh c&atilde;i về Jerusalem.</p>\r\n\r\n<p>Quyết định c&ocirc;ng nhận Jerusalem l&agrave; thủ đ&ocirc; Israel m&agrave; &ocirc;ng Trump đưa ra tuần trước đ&atilde; chấm dứt ch&iacute;nh s&aacute;ch m&agrave; Mỹ đ&atilde; ki&ecirc;n tr&igrave; theo đuổi suốt nhiều thập kỷ ở Trung Đ&ocirc;ng. Động th&aacute;i n&agrave;y cũng l&agrave;m dấy l&ecirc;n l&agrave;n s&oacute;ng giận dữ trong thế giới Hồi gi&aacute;o, khiến Mỹ c&oacute; nguy cơ đ&aacute;nh mất những đồng minh, đối t&aacute;c quan trọng trong khu vực.</p>\r\n\r\n<p>Ph&aacute;t biểu tại một cuộc họp b&aacute;o ở Ankara, Putin cho rằng quyết định của Trump &quot;kh&ocirc;ng gi&uacute;p giải quyết vấn đề Trung Đ&ocirc;ng m&agrave; ngược lại c&ograve;n g&acirc;y bất ổn với t&igrave;nh h&igrave;nh vốn đ&atilde; kh&oacute; khăn trong khu vực&quot;. &Ocirc;ng kh&ocirc;ng qu&ecirc;n khẳng định lập trường của Moscow l&agrave; vấn đề Jerusalem phải được giải quyết th&ocirc;ng qua đ&agrave;m ph&aacute;n giữa người Palestine với Israel theo nghị quyết của Li&ecirc;n Hợp Quốc.</p>\r\n\r\n<p>Chiến thắng của Nga tại Syria cho thấy một điều rằng ảnh hưởng của Moscow với t&igrave;nh h&igrave;nh ch&iacute;nh trị ở Trung Đ&ocirc;ng đang ng&agrave;y c&agrave;ng lớn v&agrave; tất cả c&aacute;c cường quốc trong khu vực, kể cả Israel, đều sẽ phải t&iacute;nh đến nước Nga trong bất cứ giải ph&aacute;p ch&iacute;nh trị n&agrave;o sau n&agrave;y.</p>\r\n', 7, 1, '2017-12-16 23:40:21', 'The Gioi', 'hinhanh/2017/12/The Gioi/thegioi4.jpg');
INSERT INTO `posts` (`id`, `title`, `content`, `user_id`, `is_public`, `createdate`, `theme`, `url_hinhanh`) VALUES
(68, 'Phi cơ, binh sĩ Nga ở Syria trở về tổ quốc', '<h2>Lực lượng cảnh s&aacute;t qu&acirc;n sự Nga c&ugrave;ng c&aacute;c phi cơ cảnh b&aacute;o sớm, oanh tạc cơ đ&atilde; trở về Nga sau khi ho&agrave;n th&agrave;nh nhiệm vụ ở Syria.</h2>\r\n\r\n<p>Bộ Quốc ph&ograve;ng Nga th&ocirc;ng b&aacute;o một tiểu đo&agrave;n cảnh s&aacute;t qu&acirc;n sự Nga đ&atilde; từ Syria về Makhachkala, Cộng h&ograve;a Dagestan, ng&agrave;y 12/12. Tiểu đo&agrave;n tiếp tục l&ecirc;n phi cơ vận tải về Nga v&agrave; được người th&acirc;n, c&aacute;c quan chức qu&acirc;n đội ch&agrave;o đ&oacute;n khi hạ c&aacute;nh,&nbsp;<em>RT</em>&nbsp;đưa tin.</p>\r\n\r\n<p>Nga triển khai cảnh s&aacute;t qu&acirc;n sự tới Syria hồi th&aacute;ng 12/2016 để đảm bảo an ninh tại c&aacute;c v&ugrave;ng giảm căng thẳng. Lực lượng n&agrave;y đ&atilde; hộ tống hơn 1.700 đo&agrave;n xe nh&acirc;n đạo v&agrave; giữ g&igrave;n an ninh cho c&aacute;c hoạt động cứu trợ.</p>\r\n\r\n<p>Một trong số những phi cơ qu&acirc;n sự Nga đầu ti&ecirc;n trở về từ Syria l&agrave; m&aacute;y bay kiểm so&aacute;t v&agrave; cảnh b&aacute;o sớm Beriev A-50. C&aacute;c oanh tạc cơ tầm xaT u-22M3, triển khai tới Bắc Ossetia trong chiến dịch qu&acirc;n sự, cũng về căn cứ gần Kaluga.</p>\r\n\r\n<p>&quot;Họ đ&atilde; ho&agrave;n th&agrave;nh nhiệm vụ tấn c&ocirc;ng những mục ti&ecirc;u khủng bố ở Cộng h&ograve;a Arab Syria&quot;, Bộ Quốc ph&ograve;ng Nga cho biết nhưng kh&ocirc;ng tiết lộ số lượng Tu-22M3.</p>\r\n\r\n<p>25 phi cơ c&ugrave;ng lực lượng đặc nhiệm, cảnh s&aacute;t qu&acirc;n sự v&agrave; c&ocirc;ng binh Nga rời Syria theo lệnh từ Tổng thống Nga Vladimir Putin h&ocirc;m 11/12. Lầu Năm G&oacute;c nghi ngờ tuy&ecirc;n bố của &ocirc;ng Putin, cho biết họ &quot;kh&ocirc;ng thấy c&oacute; sự r&uacute;t qu&acirc;n đ&aacute;ng kể n&agrave;o của lực lượng chiến đấu Nga&quot;.</p>\r\n\r\n<p>Nga bắt đầu chiến dịch qu&acirc;n sự tại Syria, theo đề nghị từ Tổng thống Bashar al-Assad, từ th&aacute;ng 9/2015. Ng&agrave;y 6/12, Nga th&ocirc;ng b&aacute;o giải ph&oacute;ng Syria khỏi Nh&agrave; nước Hồi gi&aacute;o (IS) v&agrave; &quot;mọi đơn vị khủng bố của IS&quot; đ&atilde; bị ti&ecirc;u diệt. Sau khi r&uacute;t qu&acirc;n, Nga vẫn duy tr&igrave; hoạt động tại căn cứ Hmeimim, cơ sở hải qu&acirc;n Tartus v&agrave; Trung t&acirc;m T&aacute;i h&ograve;a giải Syria của Moscow ở Syria.</p>\r\n', 7, 1, '2017-12-16 23:41:56', 'The Gioi', 'hinhanh/2017/12/The Gioi/thegioi2.jpg'),
(69, 'Ảnh mới rò rỉ hé lộ thiết kế camera kép của Galaxy S9 Plus', 'SK Telecom, nhà mạng lớn nhất Hàn Quốc, cho biết số iPhone X của họ đã được đặt hàng hết chỉ trong ba phút.\r\n\r\nTuy không tiết lộ cụ thể, SK Telecom cho biết số lượng iPhone X mà họ nhận được chỉ bằng một nửa so với iPhone 8 và 8 Plus. Phiên bản X màu bạc được yêu thích nhất, khi đã được đặt mua hết trong một phút.\r\n\r\nNăm ngoái, iPhone 7 cần tới 20 phút mới hết hàng tại nhà mạng này.\r\nTheo GSM Arena, có khoảng 150.000 chiếc iPhone X được phân phối tại Hàn Quốc đợt này. Các chuyên gia phân tích cho biết, iPhone 8 chỉ đạt được khoảng 60-70% sự quan tâm so với iPhone 7 do sự xuất hiện của iPhone X.\r\n\r\nViệc iPhone X bán chạy ở Hàn Quốc được đánh giá là điều thú vị vì sản phẩm có giá rất cao so, khoảng 1.237 USD cho phiên bản 64 GB, trong khi ở Mỹ là 999 USD. Bên cạnh đó, Hàn Quốc còn là "sân nhà" của LG và Samsung - hãng điện thoại lớn nhất thế giới.', 7, 1, '2017-12-16 23:44:33', 'Cong nghe', 'hinhanh/2017/12/Cong nghe/congnghe2.jpg'),
(71, 'Những điện thoại có camera tốt nhất 2017', '<h2>Google Pixel 2, iPhone X, Galaxy Note8 l&agrave; ba trong số những smartphone được đ&aacute;nh gi&aacute; l&agrave; chụp ảnh đẹp nhất năm 2017.</h2>\r\n\r\n<p><strong>1. Google Pixel 2</strong></p>\r\n\r\n<p>L&agrave; một trong những website đ&aacute;nh gi&aacute; về khả năng chụp ảnh uy t&iacute;n nhất hiện nay, điểm số m&agrave; DxOMark lu&ocirc;n c&oacute; độ tin cậy cao. Hiện tại, tr&ecirc;n bảng xếp hạng về khả năng chụp ảnh thiết bị di động của website n&agrave;y, Google Pixel 2 đang đứng đầu với số điểm l&ecirc;n tới 98, qua đ&oacute; trở th&agrave;nh smartphone chụp ảnh tốt nhất. Được trang bị cảm biến&nbsp;cảm biến 12 megapixel, khẩu độ f/1.8, đi k&egrave;m c&ocirc;ng nghệ tự động lấy n&eacute;t pha k&eacute;p (Dual Pixel), ổn định quang học (OIS), HDR+... Pixel 2 c&oacute; thể chụp ảnh độ n&eacute;t cao, khả năng c&acirc;n bằng dải s&aacute;ng v&agrave; độ t&aacute;i tạo m&agrave;u, tạo hiệu ứng chiều s&acirc;u khi chụp ch&acirc;n dung, tốc độ lấy n&eacute;t... Ch&iacute;nh v&igrave; vậy, khả năng chụp ảnh của m&aacute;y đạt tới 99 điểm. Trong khi đ&oacute;, việc quay video cũng mang lại kết quả tốt với khả năng lấy n&eacute;t v&agrave; bắt n&eacute;t chủ thể nhanh, chống rung tốt, kiểm so&aacute;t độ nhiễu v&agrave; c&acirc;n bằng trắng ổn. Pixel 2 đạt 96 điểm ở khả năng quay phim.</p>\r\n\r\n<p><strong>2. iPhone X</strong></p>\r\n\r\n<p>Chiếc smartphone kỷ niệm 10 năm ng&agrave;y iPhone ra đời đứng thứ hai trong bảng xếp hạng những smartphone chụp ảnh đẹp nhất hiện nay với số điểm tổng thể l&ecirc;n tới 97.&nbsp;Với cụm camera k&eacute;p đều 12 megapixel, khẩu độ f/1.8, chống rung OIS... khả năng chụp ảnh của m&aacute;y thậm ch&iacute; vượt qua Pixel 2 với 101 điểm, nhờ hỗ trợ chụp đ&ecirc;m đẹp, phơi s&aacute;ng tốt, chụp ảnh HDR tốt, c&acirc;n bằng trắng ch&iacute;nh x&aacute;c ở mọi điều kiện m&ocirc;i trường, hiệu ứng bokeh tự nhi&ecirc;n... Tuy nhi&ecirc;n, m&aacute;y vẫn lấy n&eacute;t chậm trong một số trường hợp v&agrave; dễ bị mắt đỏ khi bật flash.&nbsp;M&aacute;y chỉ đạt 89 điểm khi quay video, do dễ bị nhiễu, độ sắc n&eacute;t k&eacute;m&nbsp;khi &aacute;nh s&aacute;ng yếu v&agrave; h&igrave;nh ảnh dễ bị rung khi di chuyển.</p>\r\n\r\n<p><strong>3. Huawei Mate 10 Pro</strong></p>\r\n\r\n<p>Sức mạnh camera của Mate 10 Pro cũng kh&ocirc;ng thua k&eacute;m iPhone X với 97 điểm. Camera k&eacute;p 12 megapixel v&agrave; 20 megapixel với sự hỗ trợ về c&ocirc;ng nghệ của Leica gi&uacute;p m&aacute;y đạt 100 điểm về chụp ảnh. Ảnh chụp từ m&aacute;y c&oacute; ưu điểm l&agrave; chi tiết tốt (kể cả &aacute;nh s&aacute;ng yếu), tự động lấy n&eacute;t nhanh, ảnh giữ chi tiết khi ph&oacute;ng to, chụp bokeh tốt. Khả năng quay video của m&aacute;y đạt 91 điểm nhờ lấy n&eacute;t tự động ch&iacute;nh x&aacute;c, theo d&otilde;i chủ thể tốt, chống rung, m&agrave;u sắc trung thực.</p>\r\n\r\n<p><strong>4. iPhone 8 Plus</strong></p>\r\n\r\n<p>Với tổng điểm 94, iPhone 8 Plus nổi bật với khả năng chụp HDR &quot;tốt hơn bất kỳ thiết bị n&agrave;o đ&atilde; thử nghiệm trước đ&oacute;&quot;. M&aacute;y cũng hỗ trợ nhận diện khu&ocirc;n mặt ch&iacute;nh x&aacute;c, giữ độ chi tiết khi ph&oacute;ng to, chụp bokeh. Trong khi đ&oacute;, độ ổn định khi quay, m&agrave;u sắc h&igrave;nh ảnh đẹp kể cả khi c&oacute; &aacute;nh s&aacute;ng mạnh l&agrave; ưu điểm khi quay phim tr&ecirc;n iPhone 8 Plus. Điểm chụp ảnh v&agrave; quay video tr&ecirc;n thiết bị n&agrave;y lần lượt l&agrave; 96 v&agrave; 89.</p>\r\n\r\n<p><strong>5. Samsung Galaxy Note8</strong></p>\r\n\r\n<p>Phablet của Samsung đạt số điểm bằng với iPhone 8 Plus nhưng khả năng chụp ảnh cao hơn với 100 điểm. Ưu điểm của cụm camera k&eacute;p 12 megapixel&nbsp; (khẩu độ f/1.7 v&agrave; f/2.4) n&agrave;y l&agrave; khả năng lấy n&eacute;t nhanh v&agrave; ch&iacute;nh x&aacute;c, ảnh hiển thị chi tiết tốt trong điều kiện &aacute;nh s&aacute;ng yếu, m&agrave;u sắc tươi s&aacute;ng v&agrave; sinh động, c&oacute; thể zoom l&ecirc;n tới 4x. Trong khi đ&oacute;, khả năng quay phim của m&aacute;y đạt 84 điểm, nhờ c&acirc;n bằng trắng tốt, m&agrave;u sắc đẹp, giảm nhiễu khi &aacute;nh s&aacute;ng yếu v&agrave; độ ổn định tốt khi quay.</p>\r\n', 7, 1, '2017-12-16 23:52:24', 'Cong nghe', 'hinhanh/2017/12/Cong nghe/congnghe9.jpg'),
(70, 'iPhone X khiến điện thoại Samsung bán chậm', '<h2>Thị phần smartphone Galaxy được dự b&aacute;o xuống dưới 20% năm tới, trong khi Apple tăng trưởng.</h2>\r\n\r\n<p>Theo h&atilde;ng nghi&ecirc;n cứu&nbsp;<em>Strategy</em>, doanh số smartphone của Samsung sẽ tăng trưởng chậm nhất năm tới trong số c&aacute;c nh&agrave; sản xuất di động h&agrave;ng đầu hiện nay. H&atilde;ng điện tử H&agrave;n Quốc được dự đo&aacute;n sẽ đạt thị phần 20,5% trong năm nay, với 319 triệu m&aacute;y b&aacute;n ra.</p>\r\n\r\n<p>Tuy nhi&ecirc;n năm tới, &quot;miếng b&aacute;nh&quot; smartphone của Samsung c&oacute; thể thu hẹp c&ograve;n 19,2%, khi lượng m&aacute;y l&ecirc;n kệ giảm xuống 315 triệu chiếc. H&atilde;ng nghi&ecirc;n cứu Mỹ cho rằng nguy&ecirc;n nh&acirc;n sụt giảm tr&ecirc;n l&agrave; sức &eacute;p cạnh tranh từ điện thoại cao cấp của Apple v&agrave; c&aacute;c mẫu smartphone Trung Quốc.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; lần đầu ti&ecirc;n Samsung c&oacute; thể phải đối mặt với doanh số điện thoại đi xuống kể từ khi h&atilde;ng tr&igrave;nh l&agrave;ng thế hệ Galaxy S đầu ti&ecirc;n v&agrave;o 2010, ngoại trừ sự cố năm ngo&aacute;i khi Samsung phải thu hồi Galaxy Note7, sự cố khiến c&ocirc;ng ty b&aacute;n được 300 triệu m&aacute;y với 20,8% thị phần.</p>\r\n\r\n<p><em>BGR</em>&nbsp;cũng cho rằng Samsung sẽ phải &quot;đ&aacute;nh vật&quot; với iPhone X của Apple trong năm tới. H&atilde;ng c&ocirc;ng nghệ Mỹ đang tăng cường sản xuất để đ&aacute;p ứng nhu cầu kh&ocirc;ng ngừng tăng của kh&aacute;ch h&agrave;ng với mẫu điện thoại OLED mới. &quot;Doanh số mạnh mẽ của iPhone X c&oacute; thể l&agrave;m tổn thương Samsung&quot;, trang n&agrave;y b&igrave;nh luận.</p>\r\n\r\n<p>C&aacute;c c&ocirc;ng ty smartphone h&agrave;ng đầu kh&aacute;c, bao gồm Apple, Huawei, Oppo v&agrave; Xiaomi, được dự đo&aacute;n sẽ duy tr&igrave; thị phần hoặc tăng trưởng v&agrave;o năm tới. Apple dự kiến sẽ tăng thị phần từ 14% l&ecirc;n 14,3%, doanh số b&aacute;n h&agrave;ng tăng từ 218 triệu chiếc l&ecirc;n 234 triệu chiếc năm tới.</p>\r\n\r\n<p>Nh&agrave; sản xuất di động lớn thứ ba, Huawei, dự kiến đạt thị phần 10% năm tới, với lượng m&aacute;y l&ecirc;n kệ tăng từ 156 triệu m&aacute;y l&ecirc;n 164 triệu m&aacute;y. &quot;Đồng hương&quot; Oppo sẽ c&oacute; 7,8% thị phần năm nay v&agrave; duy tr&igrave; đến năm sau, c&ograve;n Xiaomi tăng từ 7,4% l&ecirc;n 6.1% trong c&ugrave;ng kỳ.</p>\r\n\r\n<p>Theo c&aacute;c chuy&ecirc;n gia, Samsung cần phải t&igrave;m một hướng đi đột ph&aacute; khi c&aacute;c c&ocirc;ng ty Trung Quốc đang củng cố vị tr&iacute; của m&igrave;nh tại những thị trường mới nổi, bao gồm Trung Quốc v&agrave; Ấn Độ, bằng c&aacute;c sản phẩm gi&aacute; rẻ nhưng đ&aacute;ng với số tiền.</p>\r\n\r\n<p>&quot;Từ khi c&aacute;c c&ocirc;ng ty Trung Quốc mở rộng sự hiện diện của họ tr&ecirc;n c&aacute;c thị trường smartphone đang ph&aacute;t triển, tầm ảnh hưởng của Samsung tr&ecirc;n to&agrave;n cầu ng&agrave;y c&agrave;ng giảm, trừ khi h&atilde;ng t&igrave;m được hướng đi cạnh tranh được với c&aacute;c nh&agrave; sản xuất Trung Quốc&quot;,&nbsp;Lee Byung Tae, Gi&aacute;o sư Viện Khoa học v&agrave; C&ocirc;ng nghệ n&acirc;ng cao H&agrave;n Quốc, đ&aacute;nh gi&aacute;.</p>\r\n', 7, 1, '2017-12-16 23:49:20', 'Cong nghe', 'hinhanh/2017/12/Cong nghe/congnghe6.jpg'),
(72, 'TV OLED ở Việt Nam ngày càng rẻ', '<h2>Thay v&igrave; phải bỏ ra h&agrave;ng trăm triệu đồng như c&aacute;c năm trước, gi&aacute; khởi điểm của TV OLED năm nay chỉ c&ograve;n v&agrave;i chục triệu đồng.</h2>\r\n\r\n<p>Nếu những năm trước, TV OLED chỉ xuất hiện l&aacute;c đ&aacute;c tr&ecirc;n thị trường với số lượng đếm tr&ecirc;n đầu ng&oacute;n tay, th&igrave; năm nay, thị trường dịp cuối năm đang rất s&ocirc;i động nhờ sự c&oacute; mặt của h&agrave;ng loạt TV sử dụng c&ocirc;ng nghệ m&agrave;n h&igrave;nh thế hệ mới n&agrave;y.</p>\r\n\r\n<p>L&agrave; h&atilde;ng khởi đầu, LG vẫn l&agrave; thương hiệu c&oacute; nhiều mẫu OLED nhất ở Việt Nam hiện giờ khi c&oacute; tới 10 d&ograve;ng sản phẩm v&agrave; 15 mẫu kh&aacute;c nhau nếu t&iacute;nh ri&ecirc;ng từng k&iacute;ch cỡ m&agrave;n h&igrave;nh. Trong đ&oacute;,&nbsp;<a href="https://sohoa.vnexpress.net/danh-gia/thiet-bi-nghe-nhin/oled-w7-tv-4k-dan-tuong-mong-nhu-kinh-3679137.html">W7</a>, E7, G7 v&agrave; C7 l&agrave; đời mới với c&aacute;c lựa chọn từ 55, 65 cho tới 77 inch. Tuy nhi&ecirc;n, ph&acirc;n kh&uacute;c OLED năm nay kh&ocirc;ng c&ograve;n l&agrave; cuộc chơi của ri&ecirc;ng t&ecirc;n tuổi H&agrave;n Quốc như trước bởi c&oacute; sự g&oacute;p mặt của nhiều h&atilde;ng TV.</p>\r\n\r\n<p>Sony đưa về Việt Nam bộ đ&ocirc;i TV OLED đầu ti&ecirc;n của m&igrave;nh,&nbsp;<a href="https://sohoa.vnexpress.net/tin-tuc/san-pham/oled-tv-dau-tien-cua-sony-ve-viet-nam-3583690.html">Bravia A1</a>, với hai k&iacute;ch thước m&agrave;n h&igrave;nh 55 v&agrave; 65 inch. Đồng hương tới từ Nhật, Panasonic, c&ograve;n c&oacute; nhiều mẫu OLED hơn khi sở hữu c&aacute;c model EZ950 k&iacute;ch thước 55 - 65 inch v&agrave; EZ1000 k&iacute;ch cỡ 77 inch.<br />\r\n<br />\r\nThậm ch&iacute;, một thương hiệu Việt mới nổi, Asanzo, cũng tỏ ra h&agrave;o hứng với OLED khi vừa tr&igrave;nh l&agrave;ng trong th&aacute;ng 12 d&ograve;ng TV đầu ti&ecirc;n sử dụng c&ocirc;ng nghệ n&agrave;y,&nbsp;<a href="https://sohoa.vnexpress.net/photo/san-pham/tv-oled-dau-tien-cua-asanzo-gia-tu-425-trieu-dong-3683726.html">X series</a>, với k&iacute;ch thước m&agrave;n h&igrave;nh 55 v&agrave; 65 inch.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, thị trường OLED năm nay s&ocirc;i động c&ograve;n nhờ gi&aacute; b&aacute;n. Nếu so với c&ugrave;ng thời điểm năm ngo&aacute;i, gi&aacute; của d&ograve;ng TV c&ocirc;ng nghệ n&agrave;y đ&atilde; thấp hơn rất nhiều, l&ecirc;n tới h&agrave;ng chục triệu đồng.<br />\r\n<br />\r\nV&iacute; dụ, model LG EG920T 55 inch đời 2016 tại một cửa h&agrave;ng ở Nguyễn Tr&atilde;i (H&agrave; Nội) chỉ c&ograve;n gần 40 triệu đồng, rẻ hơn c&ugrave;ng thời điểm năm ngo&aacute;i khoảng 20 triệu đồng. Mẫu OLED đời s&acirc;u hơn nữa của LG, EG910T 55 inch, chỉ c&ograve;n chưa tới 30 triệu đồng, ngang với những d&ograve;ng TV LED 4K tầm trung c&ugrave;ng th&ocirc;ng số m&agrave;n h&igrave;nh như LG SJ850 hay Samsung MU6500, Sony X7500D.<br />\r\n<br />\r\nNgay cả những mẫu OLED đời mới 2017 cũng được định gi&aacute; thấp hơn nhiều so với những năm trước. V&iacute; dụ, model&nbsp;<a href="https://sohoa.vnexpress.net/photo/san-pham/tv-oled-4k-hdr-gia-re-nhat-cua-lg-o-vn-3682678.html">OLED 4K HDR LG C7</a>&nbsp;vừa ra chỉ khoảng 50 đến 60 triệu đồng cho k&iacute;ch thước 55 v&agrave; 65 inch. Nếu so với với model C6 năm ngo&aacute;i c&ugrave;ng th&ocirc;ng số m&agrave;n h&igrave;nh, gi&aacute; hiện giờ đ&atilde; thấp hơn khoảng 30 triệu đồng. TV OLED của Sony v&agrave; Panasonic năm nay mới g&oacute;p mặt nhưng gi&aacute; khởi điểm cũng đều dưới 100 triệu đồng.<br />\r\n<br />\r\nNhiều mẫu OLED năm nay c&ograve;n rẻ hơn cả TV LED. Tuy nhi&ecirc;n, theo &ocirc;ng Quang Vũ, Gi&aacute;m đốc hệ thống MediaMart, vẫn cần nhiều thời gian để c&ocirc;ng nghệ m&agrave;n h&igrave;nh OLED thay thế cho LED, như LED từng thế chỗ LCD v&agrave; Plasma trước kia.</p>\r\n\r\n<p>Trong khi đ&oacute;, theo anh Nguyễn Anh Linh, một người chơi HD, 4K l&acirc;u năm v&agrave; kinh doanh trong lĩnh vực thiết bị nghe nh&igrave;n, OLED l&agrave; c&ocirc;ng nghệ TV mới c&oacute; nhiều điểm vượt trội so với TV LCD v&agrave; LED truyền thống, như về độ tương phản, khả năng t&aacute;i tạo m&agrave;u đen, m&agrave;u sắc v&agrave; ph&ugrave; hợp cho việc xem phim tại gia. Tuy nhi&ecirc;n, xu hướng chơi OLED chưa b&ugrave;ng nổ ở Việt Nam v&agrave; việc chuyển dịch từ LED l&ecirc;n c&ograve;n chậm. Đ&acirc;y l&agrave; điểm kh&aacute;c với những thị trường lớn tr&ecirc;n thế giới, OLED bắt đầu thay thế dần cho TV c&ocirc;ng nghệ cũ.</p>\r\n\r\n<p>Theo anh Linh, thực tế do c&ocirc;ng nghệ OLED ở Việt Nam c&ograve;n hạn chế với phần đ&ocirc;ng người mua TV trong nước. Mức gi&aacute; của ch&uacute;ng vẫn nằm trong nh&oacute;m TV cao cấp khi l&ecirc;n tới h&agrave;ng chục triệu đồng. B&ecirc;n cạnh đ&oacute;, nếu so với TV LED truyền thống, k&iacute;ch thước của OLED cũng chưa đa dạng khi phần lớn l&agrave; 55 v&agrave; 65 inch, lớn nhất mới chỉ 77 inch v&agrave; chưa c&oacute; model cỡ nhỏ. Trong khi doanh số ti&ecirc;u thụ TV ch&iacute;nh ở Việt Nam vẫn l&agrave; c&aacute;c model dưới 50 inch.</p>\r\n\r\n<p>OLED l&agrave; c&aacute;ch viết tắt của cụm từ tiếng Anh Organic Light-Emitting Diode, hay c&ograve;n gọi l&agrave; đ&egrave;n đi-ốt ph&aacute;t quang hữu cơ. Kh&aacute;c biệt của TV OLED so với LED th&ocirc;ng thường l&agrave; m&agrave;n h&igrave;nh si&ecirc;u mỏng, kh&ocirc;ng c&ograve;n hiện tượng hở s&aacute;ng v&agrave; c&oacute; thể t&aacute;i hiệu m&agrave;u đen ở mức tuyệt đối v&igrave; kh&ocirc;ng cần đến đ&egrave;n n&egrave;n. Tuy nhi&ecirc;n, kể từ khi c&oacute; mặt tr&ecirc;n thị trường, OLED vẫn được đ&aacute;nh gi&aacute; l&agrave; d&ograve;ng TV c&oacute; gi&aacute; b&aacute;n đắt đỏ.<br />\r\n<br />\r\nSau ba năm c&oacute; mặt ở Việt Nam, thị phần của loại TV n&agrave;y vẫn c&ograve;n rất nhỏ, chưa tới hai chữ số. Nhưng với sự xuất hiện ồ ạt mẫu m&atilde; mới v&agrave; gi&aacute; b&aacute;n hạ nhiều, thị phần TV OLED năm nay hứa hẹn tăng trưởng mạnh v&agrave;o dịp cuối năm nay v&agrave; Tết sắp tới, c&oacute; thể l&ecirc;n tới 30%.</p>\r\n', 1, 1, '2017-12-16 23:55:19', 'Cong nghe', 'hinhanh/2017/12/Cong nghe/congnghe11.jpg'),
(73, 'Honda Air Blade mới giá từ 38 triệu tại Việt Nam', '<h2>Mẫu xe ga 125 ph&acirc;n khối lần đầu ti&ecirc;n trang bị hệ thống kh&oacute;a th&ocirc;ng minh, đồng thời thiết kế lại mặt đồng hồ v&agrave; tem xe.</h2>\r\n\r\n<p>Trừ bản ti&ecirc;u chuẩn, Air Blade 125 c&oacute; th&ecirc;m tiện &iacute;ch mới l&agrave; hệ thống kh&oacute;a smartkey tr&ecirc;n ba phi&ecirc;n bản gồm cao cấp, sơn từ t&iacute;nh cao cấp v&agrave; phi&ecirc;n bản kỷ niệm 10 năm. Sau SH, SH Mode, v&agrave; Lead, đến lượt Air Blade lắp loại kh&oacute;a n&agrave;y.</p>\r\n\r\n<p>Với smartkey, hệ thống điện của xe được mở/tắt th&ocirc;ng qua kết nối kh&ocirc;ng d&acirc;y từ thiết bị điều khiển Fob đến bộ vi xử l&yacute; trung t&acirc;m ECU của hệ thống kh&oacute;a th&ocirc;ng minh v&agrave; xe. C&aacute;c phương thức đấu điện hay ph&aacute; ổ kh&oacute;a được ngăn chặn, tối ưu khả năng chống trộm. Hệ thống cũng x&aacute;c định vị tr&iacute; xe giống &ocirc;t&ocirc;.</p>\r\n\r\n<p>C&aacute;c phi&ecirc;n bản ti&ecirc;u chuẩn, cao cấp v&agrave; bản kỷ niệm 10 năm th&ecirc;m tem xe mới kiểu thể thao, mặt đồng hồ cũng vậy. Phi&ecirc;n bản cao cấp từ t&iacute;nh th&ecirc;m họa tiết 3D lập thể. Tr&ecirc;n cả ba phi&ecirc;n bản cao cấp, cao cấp sơn từ t&iacute;nh v&agrave; phi&ecirc;n bản kỷ niệm 10 năm, logo Air Blade 3D mạ cr&ocirc;m.&nbsp;</p>\r\n\r\n<p>Honda Air Blade phi&ecirc;n bản mới v&agrave; bản đặc biệt 10 năm sẽ b&aacute;n ra từ ng&agrave;y 26/12. Trong đ&oacute; bản 10 năm kh&aacute;c biệt ở lớp sơn mờ với tem xe v&agrave; logo ri&ecirc;ng c&ugrave;ng d&ograve;ng chữ &quot;10 Anniversary Since 2007&quot;.</p>\r\n\r\n<p>Honda Air Blade vẫn sử dụng động cơ 125 ph&acirc;n khối, c&ocirc;ng suất 11,26 m&atilde; lực, m&ocirc;-men xoắn 11,26 Nm.&nbsp;</p>\r\n\r\n<p>Mức gi&aacute; cho bản ti&ecirc;u chuẩn kh&ocirc;ng trang bị smartkey vẫn l&agrave; 38 triệu. C&aacute;c phi&ecirc;n bản c&ograve;n lại c&oacute; smartkey, mức gi&aacute; tương ứng l&agrave; cao cấp: 40,6 triệu, sơn từ t&iacute;nh: 41,1 triệu v&agrave; kỷ niệm 10 năm: 41,6 triệu.</p>\r\n\r\n<p>Đối thủ lớn của Honda Air Blade trước đ&acirc;y l&agrave; Nouvo, nhưng sau đ&oacute; Yamaha khai tử d&ograve;ng xe n&agrave;y, thay bằng NVX125, c&oacute; gi&aacute; 41-41,5 triệu.</p>\r\n', 1, 1, '2017-12-16 23:56:57', 'Cong nghe', 'hinhanh/2017/12/Cong nghe/congnghe12.jpg'),
(74, 'Nissan Terra lộ diện - đối thủ mới của Toyota Fortuner', '<h2>Mẫu SUV 7 chỗ mới sẽ l&agrave; vũ kh&iacute; của Nissan để đấu với Toyota Fortuner v&agrave; Mitsubishi Pajero Sport, b&aacute;n ra chủ yếu tại Trung Quốc.</h2>\r\n\r\n<p>Sau một loạt t&ecirc;n gọi được c&acirc;n nhắc, từ XTerra (mẫu xe vốn được b&aacute;n tại Mỹ cho tới 2015), hay Paladin (t&ecirc;n gọi sản phẩm ở Nhật Bản), th&igrave; h&igrave;nh ảnh mới nhất cho thấy quyết định cuối c&ugrave;ng của Nissan l&agrave; Terra.</p>\r\n\r\n<p>Toyota Fortuner, Ford Everest v&agrave; Mitsubishi Pajero Sport đều chia sẻ kết cấu c&ugrave;ng một phi&ecirc;n bản b&aacute;n tải c&ugrave;ng h&atilde;ng, th&igrave; Nissan Terra cũng c&oacute; nền tảng tương tự. Terra ph&aacute;t triển dựa tr&ecirc;n bộ khung gầm của mẫu pick-up Navara.</p>\r\n\r\n<p>Lưới tản nhiệt v&agrave; đ&egrave;n pha của Terra thể hiện ng&ocirc;n ngữ thiết kế mới nhất của Nissan. Trong khi phần đu&ocirc;i đơn giản với đ&egrave;n hậu nằm ngang với thanh ngang mạ cr&ocirc;m ở nắp cửa sau. Xe trong ảnh dường như l&agrave; bản thấp cấp khi kh&ocirc;ng c&oacute; đ&egrave;n sương m&ugrave; ph&iacute;a trước.</p>\r\n\r\n<p>Ngo&agrave;i đ&egrave;n sương m&ugrave;, sự kh&aacute;c biệt về ngoại h&igrave;nh giữa bản ti&ecirc;u chuẩn v&agrave; bản cao cấp được cho l&agrave; c&ograve;n ở đ&egrave;n pha t&iacute;ch hợp LED, ốp gương chiếu hậu v&agrave; cửa sổ trời.</p>\r\n\r\n<p>Chiếc SUV 7 chỗ dự kiến trang bị động cơ xăng 2,5 l&iacute;t c&ocirc;ng suất 184 m&atilde; lực d&agrave;nh cho thị trường Trung Quốc. T&ugrave;y chọn dẫn động cầu sau hoặc 4 b&aacute;nh. Dự kiến sẽ c&oacute; th&ecirc;m c&aacute;c bản động cơ dầu 2,3 l&iacute;t 4 xi-lanh với c&ocirc;ng suất 158 v&agrave; 187 m&atilde; lực.</p>\r\n\r\n<p>Theo kế hoạch, Nissan sẽ giới thiệu Terra tại triển l&atilde;m &ocirc;t&ocirc; Bắc Kinh diễn ra v&agrave;o th&aacute;ng 4/2018 trước khi b&aacute;n ra tại thị trường n&agrave;y. Mẫu SUV 7 chỗ cũng được đ&aacute;nh gi&aacute; l&agrave; sản phẩm tiềm năng ở những thị trường kh&aacute;c ngay tại ch&acirc;u &Aacute;, như Ấn Độ, Indonesia hay Th&aacute;i Lan.</p>\r\n', 1, 1, '2017-12-16 23:58:37', 'Cong nghe', 'hinhanh/2017/12/Cong nghe/congnghe13.jpg'),
(75, 'Việt Nam giành ba huy chương bạc Olympic khoa học trẻ quốc tế', '<h2>Hai huy chương bạc thuộc về học sinh chuy&ecirc;n H&agrave; Nội - Amsterdam, chiếc c&ograve;n lại của THPT chuy&ecirc;n Nguyễn Huệ.</h2>\r\n\r\n<p>Ng&agrave;y 15/12, Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo H&agrave; Nội cho biết, đo&agrave;n học sinh thủ đ&ocirc; đại diện Việt Nam tham dự kỳ thi Olympic khoa học trẻ quốc tế lần thứ 14 tại H&agrave; Lan từ ng&agrave;y 2 đến 15/12 đ&atilde; gi&agrave;nh ba huy chương bạc v&agrave; ba huy chương đồng.</p>\r\n\r\n<p>C&aacute;c em Lương Hiền Nga, lớp 10 H&oacute;a 1 THPT chuy&ecirc;n H&agrave; Nội - Amsterdam; Đỗ Cao Minh Ch&acirc;u lớp 10 Sinh THPT chuy&ecirc;n H&agrave; Nội - Amsterdam v&agrave; em Nguyễn Trọng Khải lớp 10 H&oacute;a 1 trường THPT chuy&ecirc;n Nguyễn Huệ đ&atilde; gi&agrave;nh huy chương bạc. Ba huy chương đồng thuộc về Nguyễn Đức Hiếu, L&ecirc; Ho&agrave;ng Minh, Trần Kh&aacute;nh Vy, lớp 10 H&oacute;a 1 trường THPT chuy&ecirc;n H&agrave; Nội - Amsterdam.</p>\r\n\r\n<p>Đề thi năm nay đ&ograve;i hỏi sự suy luận, ph&acirc;n t&iacute;ch v&agrave; c&aacute;c kỹ năng khoa học ở mức độ cao. C&aacute;c c&acirc;u hỏi trong b&agrave;i thi đều hướng đến một chủ đề chung l&agrave; &ldquo;Nước v&agrave; sự bền vững&rdquo;. Th&iacute; sinh phải giải quyết vấn đề thực tiễn con người gặp phải trong qu&aacute; tr&igrave;nh xử l&yacute; nước, chống biến đổi kh&iacute; hậu v&agrave; bảo vệ Tr&aacute;i Đất.</p>\r\n\r\n<p>Olympic khoa học trẻ quốc tế lần thứ 14 thu h&uacute;t sự tham dự của 50 nước v&agrave; v&ugrave;ng l&atilde;nh thổ với 279 học sinh. Đ&acirc;y l&agrave; năm thứ 8 li&ecirc;n tục, Sở Gi&aacute;o dục H&agrave; Nội được Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo ủy quyền, giao tr&aacute;ch nhiệm tổ chức th&agrave;nh lập đội tuyển quốc gia đại diện cho học sinh Việt Nam tham dự.</p>\r\n\r\n<p>Năm 2016, 5/6 học sinh H&agrave; Nội đ&atilde; đoạt giải, trong đ&oacute; c&oacute; hai huy chương v&agrave;ng, hai huy chương bạc v&agrave; một huy chương đồng. Đo&agrave;n học sinh Việt Nam nằm trong top 5 đo&agrave;n c&oacute; th&agrave;nh t&iacute;ch xuất sắc cuộc thi.</p>\r\n', 1, 1, '2017-12-17 00:00:22', 'Giao Duc', 'hinhanh/2017/12/Giao Duc/giaoduc12.jpg');

